contracts/CreditRegistryV1_1.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";

/**
 * @title CreditRegistryV1_1
 * @notice Credit score registry with optimistic oracle mechanism
 * @dev Enhanced v1.1 model with 8-factor scoring (300-850 range)
 *
 * OPTIMISTIC ORACLE DESIGN:
 * 1. Score attester posts score + merkle root
 * 2. Challenge period (1 hour default)
 * 3. Anyone can challenge with bond
 * 4. If challenged, attester must provide merkle proof
 * 5. Invalid scores are rejected, challenger gets reward
 *
 * PROGRESSIVE DECENTRALIZATION:
 * - Phase 1 (Launch): Team-controlled attester (transparent, auditable)
 * - Phase 2 (Month 6): Chainlink Functions integration
 * - Phase 3 (Month 12+): ZK-ML integration
 */
contract CreditRegistryV1_1 is Ownable, ReentrancyGuard {

    // ============ Structs ============

    /**
     * @notice Credit score data (v1.1 enhanced model)
     * @param score Credit score (300-850, FICO-like)
     * @param tier Credit tier (0=Subprime, 1=Fair, 2=Good, 3=Very Good, 4=Exceptional)
     * @param ltv Recommended LTV percentage (0-90)
     * @param interestRateMultiplier Interest rate multiplier (80-150 = 0.8x to 1.5x)
     * @param lastUpdated Timestamp of last score update
     * @param dataQuality Data quality (0=low, 1=medium, 2=high)
     */
    struct CreditScore {
        uint16 score;                   // 300-850
        uint8 tier;                     // 0-4
        uint8 ltv;                      // 0-90%
        uint16 interestRateMultiplier;  // 80-150 (0.8x-1.5x in basis points)
        uint64 lastUpdated;             // Timestamp
        uint8 dataQuality;              // 0-2
    }

    /**
     * @notice Score attestation with optimistic oracle data
     * @param scoreHash Hash of the score data
     * @param merkleRoot Merkle root of supporting evidence
     * @param attester Address that posted the attestation
     * @param timestamp When attestation was posted
     * @param challengeDeadline Deadline for challenges
     * @param challenged Whether this attestation has been challenged
     */
    struct ScoreAttestation {
        bytes32 scoreHash;
        bytes32 merkleRoot;
        address attester;
        uint64 timestamp;
        uint64 challengeDeadline;
        bool challenged;
        bool finalized;
    }

    /**
     * @notice Challenge to a score attestation
     * @param challenger Address that initiated the challenge
     * @param bond Amount of bond posted
     * @param reason Brief reason code for challenge
     * @param resolved Whether challenge has been resolved
     */
    struct Challenge {
        address challenger;
        uint256 bond;
        uint8 reason;
        bool resolved;
        bool successful;
    }

    // ============ State Variables ============

    /// @notice Current credit scores for each user
    mapping(address => CreditScore) public scores;

    /// @notice Pending attestations (userAddress => attestation)
    mapping(address => ScoreAttestation) public pendingAttestations;

    /// @notice Challenges (userAddress => challenge)
    mapping(address => Challenge) public challenges;

    /// @notice Authorized score attesters
    mapping(address => bool) public authorizedAttesters;

    /// @notice Challenge period duration (default 1 hour)
    uint256 public challengePeriod = 1 hours;

    /// @notice Minimum challenge bond (default 500 USDC = 500e6)
    uint256 public challengeBond = 500e6;

    /// @notice Challenge reward percentage (default 50% of bond)
    uint256 public challengeRewardBps = 5000; // 50%

    /// @notice Treasury address for failed challenge bonds
    address public treasury;

    // ============ Events ============

    event ScoreAttested(
        address indexed user,
        uint16 score,
        uint8 tier,
        bytes32 merkleRoot,
        address indexed attester,
        uint64 challengeDeadline
    );

    event ScoreChallenged(
        address indexed user,
        address indexed challenger,
        uint256 bond,
        uint8 reason
    );

    event ChallengeResolved(
        address indexed user,
        address indexed challenger,
        bool successful,
        uint256 reward
    );

    event ScoreFinalized(
        address indexed user,
        uint16 score,
        uint8 tier,
        uint8 ltv
    );

    event AttesterAuthorized(address indexed attester, bool authorized);
    event ChallengePeriodUpdated(uint256 newPeriod);
    event ChallengeBondUpdated(uint256 newBond);

    // ============ Errors ============

    error UnauthorizedAttester();
    error InvalidScore();
    error InvalidTier();
    error InvalidLTV();
    error AttestationPending();
    error NoPendingAttestation();
    error ChallengePeriodNotExpired();
    error AlreadyChallenged();
    error InsufficientBond();
    error ChallengeNotFound();
    error AlreadyResolved();
    error InvalidMerkleProof();

    // ============ Constructor ============

    constructor(address _treasury) Ownable(msg.sender) {
        require(_treasury != address(0), "Invalid treasury");
        treasury = _treasury;

        // Initially authorize deployer as attester
        authorizedAttesters[msg.sender] = true;
        emit AttesterAuthorized(msg.sender, true);
    }

    // ============ External Functions ============

    /**
     * @notice Attest to a user's credit score (optimistic oracle)
     * @param user User address
     * @param score Credit score (300-850)
     * @param tier Credit tier (0-4)
     * @param ltv Recommended LTV (0-90)
     * @param interestRateMultiplier Interest rate multiplier (80-150)
     * @param dataQuality Data quality (0-2)
     * @param merkleRoot Merkle root of supporting evidence
     */
    function attestScore(
        address user,
        uint16 score,
        uint8 tier,
        uint8 ltv,
        uint16 interestRateMultiplier,
        uint8 dataQuality,
        bytes32 merkleRoot
    ) external {
        if (!authorizedAttesters[msg.sender]) revert UnauthorizedAttester();
        if (score < 300 || score > 850) revert InvalidScore();
        if (tier > 4) revert InvalidTier();
        if (ltv > 90) revert InvalidLTV();
        if (pendingAttestations[user].attester != address(0) && !pendingAttestations[user].finalized) {
            revert AttestationPending();
        }

        // Create score hash
        bytes32 scoreHash = keccak256(abi.encode(
            user,
            score,
            tier,
            ltv,
            interestRateMultiplier,
            dataQuality,
            block.timestamp
        ));

        // Create attestation
        uint64 deadline = uint64(block.timestamp + challengePeriod);
        pendingAttestations[user] = ScoreAttestation({
            scoreHash: scoreHash,
            merkleRoot: merkleRoot,
            attester: msg.sender,
            timestamp: uint64(block.timestamp),
            challengeDeadline: deadline,
            challenged: false,
            finalized: false
        });

        emit ScoreAttested(user, score, tier, merkleRoot, msg.sender, deadline);
    }

    /**
     * @notice Challenge a pending score attestation
     * @param user User whose score is being challenged
     * @param reason Reason code for challenge
     */
    function challengeScore(address user, uint8 reason) external payable {
        ScoreAttestation storage attestation = pendingAttestations[user];
        if (attestation.attester == address(0)) revert NoPendingAttestation();
        if (attestation.challenged) revert AlreadyChallenged();
        if (block.timestamp >= attestation.challengeDeadline) revert ChallengePeriodNotExpired();
        if (msg.value < challengeBond) revert InsufficientBond();

        // Mark as challenged
        attestation.challenged = true;

        // Create challenge record
        challenges[user] = Challenge({
            challenger: msg.sender,
            bond: msg.value,
            reason: reason,
            resolved: false,
            successful: false
        });

        emit ScoreChallenged(user, msg.sender, msg.value, reason);
    }

    /**
     * @notice Resolve a challenge by providing merkle proof
     * @param user User address
     * @param score Credit score being attested
     * @param tier Credit tier
     * @param ltv LTV
     * @param interestRateMultiplier Interest rate multiplier
     * @param dataQuality Data quality
     * @param proof Merkle proof of evidence
     */
    function resolveChallenge(
        address user,
        uint16 score,
        uint8 tier,
        uint8 ltv,
        uint16 interestRateMultiplier,
        uint8 dataQuality,
        bytes32[] calldata proof
    ) external {
        ScoreAttestation storage attestation = pendingAttestations[user];
        Challenge storage challenge = challenges[user];

        if (!attestation.challenged) revert ChallengeNotFound();
        if (challenge.resolved) revert AlreadyResolved();

        // Verify score hash matches
        bytes32 scoreHash = keccak256(abi.encode(
            user,
            score,
            tier,
            ltv,
            interestRateMultiplier,
            dataQuality,
            attestation.timestamp
        ));

        require(scoreHash == attestation.scoreHash, "Score hash mismatch");

        // Verify merkle proof (simplified - in production, would verify full evidence)
        bytes32 leaf = keccak256(abi.encodePacked(user, score));
        bool validProof = MerkleProof.verify(proof, attestation.merkleRoot, leaf);

        challenge.resolved = true;

        if (validProof) {
            // Challenge failed - attester was correct
            challenge.successful = false;

            // Return bond to challenger minus penalty
            uint256 penalty = (challenge.bond * challengeRewardBps) / 10000;
            payable(challenge.challenger).transfer(challenge.bond - penalty);
            payable(treasury).transfer(penalty);

            // Finalize score
            _finalizeScore(user, score, tier, ltv, interestRateMultiplier, dataQuality);

            emit ChallengeResolved(user, challenge.challenger, false, 0);
        } else {
            // Challenge succeeded - attester was wrong
            challenge.successful = true;

            // Reward challenger
            uint256 reward = challenge.bond + (challenge.bond * challengeRewardBps) / 10000;
            payable(challenge.challenger).transfer(reward);

            // Clear invalid attestation
            delete pendingAttestations[user];

            emit ChallengeResolved(user, challenge.challenger, true, reward);
        }
    }

    /**
     * @notice Finalize a score after challenge period expires
     * @param user User address
     * @param score Credit score
     * @param tier Credit tier
     * @param ltv LTV
     * @param interestRateMultiplier Interest rate multiplier
     * @param dataQuality Data quality
     */
    function finalizeScore(
        address user,
        uint16 score,
        uint8 tier,
        uint8 ltv,
        uint16 interestRateMultiplier,
        uint8 dataQuality
    ) external {
        ScoreAttestation storage attestation = pendingAttestations[user];

        if (attestation.attester == address(0)) revert NoPendingAttestation();
        if (block.timestamp < attestation.challengeDeadline) revert ChallengePeriodNotExpired();
        if (attestation.challenged) revert AlreadyChallenged();

        // Verify score hash
        bytes32 scoreHash = keccak256(abi.encode(
            user,
            score,
            tier,
            ltv,
            interestRateMultiplier,
            dataQuality,
            attestation.timestamp
        ));

        require(scoreHash == attestation.scoreHash, "Score hash mismatch");

        _finalizeScore(user, score, tier, ltv, interestRateMultiplier, dataQuality);
    }

    /**
     * @notice Get user's current credit score
     * @param user User address
     * @return CreditScore struct
     */
    function getScore(address user) external view returns (CreditScore memory) {
        return scores[user];
    }

    /**
     * @notice Check if user has a valid, recent score
     * @param user User address
     * @param maxAge Maximum age in seconds (e.g., 30 days)
     * @return bool True if score is valid and recent
     */
    function hasValidScore(address user, uint256 maxAge) external view returns (bool) {
        CreditScore memory userScore = scores[user];
        if (userScore.score == 0) return false;
        if (block.timestamp - userScore.lastUpdated > maxAge) return false;
        return true;
    }

    // ============ Admin Functions ============

    /**
     * @notice Authorize/deauthorize a score attester
     * @param attester Attester address
     * @param authorized Whether to authorize
     */
    function setAuthorizedAttester(address attester, bool authorized) external onlyOwner {
        authorizedAttesters[attester] = authorized;
        emit AttesterAuthorized(attester, authorized);
    }

    /**
     * @notice Update challenge period
     * @param newPeriod New period in seconds
     */
    function setChallengePeriod(uint256 newPeriod) external onlyOwner {
        require(newPeriod >= 10 minutes && newPeriod <= 24 hours, "Invalid period");
        challengePeriod = newPeriod;
        emit ChallengePeriodUpdated(newPeriod);
    }

    /**
     * @notice Update challenge bond amount
     * @param newBond New bond amount
     */
    function setChallengeBond(uint256 newBond) external onlyOwner {
        require(newBond >= 100e6 && newBond <= 10000e6, "Invalid bond"); // 100-10,000 USDC
        challengeBond = newBond;
        emit ChallengeBondUpdated(newBond);
    }

    /**
     * @notice Update treasury address
     * @param newTreasury New treasury address
     */
    function setTreasury(address newTreasury) external onlyOwner {
        require(newTreasury != address(0), "Invalid treasury");
        treasury = newTreasury;
    }

    // ============ Internal Functions ============

    /**
     * @notice Internal function to finalize a credit score
     */
    function _finalizeScore(
        address user,
        uint16 score,
        uint8 tier,
        uint8 ltv,
        uint16 interestRateMultiplier,
        uint8 dataQuality
    ) internal {
        scores[user] = CreditScore({
            score: score,
            tier: tier,
            ltv: ltv,
            interestRateMultiplier: interestRateMultiplier,
            lastUpdated: uint64(block.timestamp),
            dataQuality: dataQuality
        });

        pendingAttestations[user].finalized = true;

        emit ScoreFinalized(user, score, tier, ltv);
    }

    /**
     * @notice Emergency withdrawal (only for stuck funds)
     */
    function emergencyWithdraw() external onlyOwner {
        payable(treasury).transfer(address(this).balance);
    }

    // Allow contract to receive ETH for challenges
    receive() external payable {}
}

contracts/CreditVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "./CreditRegistryV1_1.sol";
import "./ScoreOracleSimple.sol";
import "./interfaces/IAggregatorV3.sol";

/**
 * @title CreditVault
 * @notice Main lending contract with score-based LTV, grace periods, and liquidation logic
 * @dev Phase 1: Works with ScoreOracle Phase 1, simple interest, tiered grace periods
 */
contract CreditVault is Ownable, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    // ==================== STATE ====================

    CreditRegistryV1_1 public immutable registry;
    ScoreOracleSimple public oracle;

    // LTV bands (in percentage: 50 = 50%)
    uint16 public constant LTV_BRONZE = 50;    // score 300-579
    uint16 public constant LTV_SILVER = 70;    // score 580-669
    uint16 public constant LTV_GOLD = 80;      // score 670-739
    uint16 public constant LTV_PLATINUM = 90;  // score 740-850

    // Grace periods before liquidation
    uint256 public constant GRACE_BRONZE = 24 hours;
    uint256 public constant GRACE_SILVER = 36 hours;
    uint256 public constant GRACE_GOLD = 48 hours;
    uint256 public constant GRACE_PLATINUM = 72 hours;

    // Liquidation penalty: 10% total (5% to liquidator, 5% to insurance)
    uint16 public constant LIQUIDATION_PENALTY_BPS = 1000; // 10%
    uint16 public constant LIQUIDATOR_SHARE_BPS = 500;     // 5%
    uint16 public constant INSURANCE_SHARE_BPS = 500;      // 5%

    // Health factor minimum for withdrawals
    uint256 public constant MIN_HEALTH_FACTOR = 12e17; // 1.2

    struct Loan {
        address borrower;
        uint256 principal;           // borrowed amount in USD (18 decimals)
        uint256 collateralAmount;    // collateral amount in token decimals
        address collateralToken;     // which token is collateral
        uint256 aprBps;              // annual percentage rate in basis points
        uint256 startTimestamp;      // when loan started
        bool active;                 // is loan currently active
        uint256 graceStart;          // when grace period started (0 if not triggered)
    }

    struct AssetConfig {
        bool allowed;
        address priceFeed; // Chainlink price feed
    }

    mapping(uint256 => Loan) public loans;
    mapping(address => AssetConfig) public assets;
    mapping(address => uint256) public userCollateral; // user => total collateral value (USD 18 decimals)

    address public insurancePool;
    uint256 public nextLoanId;

    // ==================== EVENTS ====================

    event LoanCreated(uint256 indexed loanId, address indexed borrower, uint256 principal, uint256 collateral, address collateralToken);
    event LoanRepaid(uint256 indexed loanId, address indexed borrower, uint256 amount, uint256 remaining);
    event LoanLiquidated(uint256 indexed loanId, address indexed borrower, address indexed liquidator, uint256 collateralSeized);
    event CollateralDeposited(address indexed user, address indexed token, uint256 amount, uint256 usdValue);
    event CollateralWithdrawn(address indexed user, address indexed token, uint256 amount, uint256 usdValue);
    event AssetConfigured(address indexed token, address priceFeed, bool allowed);
    event InsurancePoolSet(address indexed pool);
    event OracleUpdated(address indexed newOracle);
    event GracePeriodStarted(uint256 indexed loanId, uint256 timestamp);

    // ==================== CONSTRUCTOR ====================

    constructor(
        address payable _registry,
        address _oracle
    ) Ownable(msg.sender) {
        require(_registry != address(0), "Invalid registry");
        require(_oracle != address(0), "Invalid oracle");

        registry = CreditRegistryV1_1(_registry);
        oracle = ScoreOracleSimple(_oracle);
    }

    // ==================== ADMIN FUNCTIONS ====================

    function setOracle(address _oracle) external onlyOwner {
        require(_oracle != address(0), "Invalid oracle");
        oracle = ScoreOracleSimple(_oracle);
        emit OracleUpdated(_oracle);
    }

    function setAsset(address token, address priceFeed, bool allowed) external onlyOwner {
        require(token != address(0), "Invalid token");
        require(priceFeed != address(0), "Invalid price feed");
        assets[token] = AssetConfig(allowed, priceFeed);
        emit AssetConfigured(token, priceFeed, allowed);
    }

    function setInsurancePool(address _pool) external onlyOwner {
        require(_pool != address(0), "Invalid pool");
        insurancePool = _pool;
        emit InsurancePoolSet(_pool);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    // ==================== USER FUNCTIONS ====================

    /**
     * @notice Deposit collateral
     * @param token Collateral token address
     * @param amount Amount to deposit (in token decimals)
     */
    function depositCollateral(address token, uint256 amount) external nonReentrant whenNotPaused {
        require(assets[token].allowed, "Asset not allowed");
        require(amount > 0, "Zero amount");

        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);

        uint256 usdValue = _tokenAmountToUsd18(token, amount);
        userCollateral[msg.sender] += usdValue;

        emit CollateralDeposited(msg.sender, token, amount, usdValue);
    }

    /**
     * @notice Withdraw collateral (only if health factor remains > 1.2)
     * @param token Collateral token address
     * @param amount Amount to withdraw (in token decimals)
     */
    function withdrawCollateral(address token, uint256 amount) external nonReentrant whenNotPaused {
        require(assets[token].allowed, "Asset not allowed");
        require(amount > 0, "Zero amount");

        uint256 usdValue = _tokenAmountToUsd18(token, amount);
        require(userCollateral[msg.sender] >= usdValue, "Insufficient collateral");

        userCollateral[msg.sender] -= usdValue;

        // Check health factor after withdrawal
        uint256 totalBorrowed = _getTotalBorrowed(msg.sender);
        if (totalBorrowed > 0) {
            uint256 healthFactor = (userCollateral[msg.sender] * 1e18) / totalBorrowed;
            require(healthFactor >= MIN_HEALTH_FACTOR, "Health factor too low");
        }

        IERC20(token).safeTransfer(msg.sender, amount);
        emit CollateralWithdrawn(msg.sender, token, amount, usdValue);
    }

    /**
     * @notice Borrow against collateral
     * @param collateralToken Token to use as collateral
     * @param collateralAmount Amount of collateral (in token decimals)
     * @param principalUsd18 Amount to borrow in USD (18 decimals)
     */
    function borrow(
        address collateralToken,
        uint256 collateralAmount,
        uint256 principalUsd18
    ) external nonReentrant whenNotPaused returns (uint256 loanId) {
        require(assets[collateralToken].allowed, "Asset not allowed");
        require(collateralAmount > 0, "Zero collateral");
        require(principalUsd18 > 0, "Zero principal");

        // Get score and determine max LTV
        ScoreOracleSimple.ScoreBreakdown memory sb = oracle.computeScore(msg.sender);
        uint16 maxLtvPercent = _maxLtvForScore(sb.overall);

        // Check LTV
        uint256 collateralUsd18 = _tokenAmountToUsd18(collateralToken, collateralAmount);
        require(principalUsd18 * 100 <= collateralUsd18 * maxLtvPercent, "Exceeds allowed LTV");

        // Get APR based on score
        uint16 apr = oracle.getAPR(sb.overall);

        // Create loan
        loanId = nextLoanId++;
        loans[loanId] = Loan({
            borrower: msg.sender,
            principal: principalUsd18,
            collateralAmount: collateralAmount,
            collateralToken: collateralToken,
            aprBps: apr,
            startTimestamp: block.timestamp,
            active: true,
            graceStart: 0
        });

        // Transfer collateral from user
        IERC20(collateralToken).safeTransferFrom(msg.sender, address(this), collateralAmount);

        // Note: Registry integration will be added in Phase 2 when CreditRegistry is enhanced

        emit LoanCreated(loanId, msg.sender, principalUsd18, collateralAmount, collateralToken);

        // TODO: Transfer borrowed funds to user (needs stablecoin pool implementation)
    }

    /**
     * @notice Repay loan (full or partial)
     * @param loanId Loan identifier
     * @param amountUsd18 Amount to repay in USD (18 decimals)
     */
    function repay(uint256 loanId, uint256 amountUsd18) external nonReentrant whenNotPaused {
        Loan storage loan = loans[loanId];
        require(loan.active, "Loan not active");
        require(amountUsd18 > 0, "Zero amount");

        uint256 debt = _calculateDebt(loanId);
        require(amountUsd18 <= debt, "Amount exceeds debt");

        // Note: Registry integration will be added in Phase 2

        // If full repayment, close loan and return collateral
        if (amountUsd18 >= debt) {
            loan.active = false;
            IERC20(loan.collateralToken).safeTransfer(loan.borrower, loan.collateralAmount);
            emit LoanRepaid(loanId, loan.borrower, amountUsd18, 0);
        } else {
            emit LoanRepaid(loanId, loan.borrower, amountUsd18, debt - amountUsd18);
        }

        // TODO: Transfer repayment funds from user (needs stablecoin integration)
    }

    /**
     * @notice Liquidate unhealthy loan
     * @param loanId Loan identifier
     */
    function liquidate(uint256 loanId) external nonReentrant whenNotPaused {
        Loan storage loan = loans[loanId];
        require(loan.active, "Loan not active");

        uint256 debt = _calculateDebt(loanId);
        uint256 collateralUsd18 = _tokenAmountToUsd18(loan.collateralToken, loan.collateralAmount);

        // Get max LTV for current score
        ScoreOracleSimple.ScoreBreakdown memory sb = oracle.computeScore(loan.borrower);
        uint16 maxLtvPercent = _maxLtvForScore(sb.overall);
        uint256 liquidationThreshold = maxLtvPercent + 10; // LTV + 10%

        // Check if liquidatable
        bool canLiquidate = (debt * 100 > collateralUsd18 * liquidationThreshold);

        // If not immediately liquidatable, check grace period
        if (!canLiquidate) {
            uint256 gracePeriod = _gracePeriodForScore(sb.overall);

            if (loan.graceStart == 0) {
                // Start grace period
                loan.graceStart = block.timestamp;
                emit GracePeriodStarted(loanId, block.timestamp);
                revert("Grace period started");
            } else {
                // Check if grace period expired
                require(block.timestamp >= loan.graceStart + gracePeriod, "Grace period not expired");
            }
        }

        // Liquidate
        loan.active = false;

        // Calculate penalty (10% of collateral)
        uint256 penaltyAmount = (loan.collateralAmount * LIQUIDATION_PENALTY_BPS) / 10000;
        uint256 liquidatorReward = (loan.collateralAmount * LIQUIDATOR_SHARE_BPS) / 10000;
        uint256 insuranceReward = (loan.collateralAmount * INSURANCE_SHARE_BPS) / 10000;
        uint256 remaining = loan.collateralAmount - penaltyAmount;

        // Transfer rewards
        IERC20(loan.collateralToken).safeTransfer(msg.sender, liquidatorReward);
        if (insurancePool != address(0)) {
            IERC20(loan.collateralToken).safeTransfer(insurancePool, insuranceReward);
        }

        // Return remaining collateral to borrower
        if (remaining > 0) {
            IERC20(loan.collateralToken).safeTransfer(loan.borrower, remaining);
        }

        // Note: Registry integration will be added in Phase 2

        emit LoanLiquidated(loanId, loan.borrower, msg.sender, loan.collateralAmount);
    }

    // ==================== VIEW FUNCTIONS ====================

    function getLoan(uint256 loanId) external view returns (Loan memory) {
        return loans[loanId];
    }

    function calculateDebt(uint256 loanId) external view returns (uint256) {
        return _calculateDebt(loanId);
    }

    function getHealthFactor(uint256 loanId) external view returns (uint256) {
        Loan memory loan = loans[loanId];
        if (!loan.active) return 0;

        uint256 debt = _calculateDebt(loanId);
        uint256 collateralUsd18 = _tokenAmountToUsd18(loan.collateralToken, loan.collateralAmount);

        return (collateralUsd18 * 1e18) / debt;
    }

    function getUserHealthFactor(address user) external view returns (uint256) {
        uint256 totalBorrowed = _getTotalBorrowed(user);
        if (totalBorrowed == 0) return type(uint256).max;

        return (userCollateral[user] * 1e18) / totalBorrowed;
    }

    // ==================== INTERNAL FUNCTIONS ====================

    function _calculateDebt(uint256 loanId) internal view returns (uint256) {
        Loan memory loan = loans[loanId];
        if (!loan.active) return 0;

        uint256 timeElapsed = block.timestamp - loan.startTimestamp;
        uint256 interest = (loan.principal * loan.aprBps * timeElapsed) / (10000 * 365 days);

        return loan.principal + interest;
    }

    function _tokenAmountToUsd18(address token, uint256 amount) internal view returns (uint256) {
        AssetConfig memory config = assets[token];
        require(config.allowed, "Asset not allowed");

        IAggregatorV3 feed = IAggregatorV3(config.priceFeed);
        (, int256 price, , , ) = feed.latestRoundData();
        require(price > 0, "Invalid price");

        uint8 decimals = feed.decimals();
        uint8 tokenDecimals = _getTokenDecimals(token);

        // Convert to 18 decimals: (amount * price * 10^18) / (10^tokenDecimals * 10^decimals)
        return (amount * uint256(price) * 1e18) / (10 ** (tokenDecimals + decimals));
    }

    function _getTokenDecimals(address token) internal view returns (uint8) {
        // Try to get decimals, fallback to 18
        try IERC20Metadata(token).decimals() returns (uint8 d) {
            return d;
        } catch {
            return 18;
        }
    }

    function _maxLtvForScore(uint16 score) internal pure returns (uint16) {
        if (score >= 740) return LTV_PLATINUM;
        if (score >= 670) return LTV_GOLD;
        if (score >= 580) return LTV_SILVER;
        return LTV_BRONZE;
    }

    function _gracePeriodForScore(uint16 score) internal pure returns (uint256) {
        if (score >= 740) return GRACE_PLATINUM;
        if (score >= 670) return GRACE_GOLD;
        if (score >= 580) return GRACE_SILVER;
        return GRACE_BRONZE;
    }

    function _getTotalBorrowed(address user) internal view returns (uint256) {
        uint256 total = 0;
        for (uint256 i = 0; i < nextLoanId; i++) {
            Loan memory loan = loans[i];
            if (loan.active && loan.borrower == user) {
                total += _calculateDebt(i);
            }
        }
        return total;
    }
}

interface IERC20Metadata {
    function decimals() external view returns (uint8);
}

