contracts/CreditRegistryV1_1.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";

/**
 * @title CreditRegistryV1_1
 * @notice Credit score registry with optimistic oracle mechanism
 * @dev Enhanced v1.1 model with 8-factor scoring (300-850 range)
 *
 * OPTIMISTIC ORACLE DESIGN:
 * 1. Score attester posts score + merkle root
 * 2. Challenge period (1 hour default)
 * 3. Anyone can challenge with bond
 * 4. If challenged, attester must provide merkle proof
 * 5. Invalid scores are rejected, challenger gets reward
 *
 * PROGRESSIVE DECENTRALIZATION:
 * - Phase 1 (Launch): Team-controlled attester (transparent, auditable)
 * - Phase 2 (Month 6): Chainlink Functions integration
 * - Phase 3 (Month 12+): ZK-ML integration
 */
contract CreditRegistryV1_1 is Ownable, ReentrancyGuard {

    // ============ Structs ============

    /**
     * @notice Credit score data (v1.1 enhanced model)
     * @param score Credit score (300-850, FICO-like)
     * @param tier Credit tier (0=Subprime, 1=Fair, 2=Good, 3=Very Good, 4=Exceptional)
     * @param ltv Recommended LTV percentage (0-90)
     * @param interestRateMultiplier Interest rate multiplier (80-150 = 0.8x to 1.5x)
     * @param lastUpdated Timestamp of last score update
     * @param dataQuality Data quality (0=low, 1=medium, 2=high)
     */
    struct CreditScore {
        uint16 score;                   // 300-850
        uint8 tier;                     // 0-4
        uint8 ltv;                      // 0-90%
        uint16 interestRateMultiplier;  // 80-150 (0.8x-1.5x in basis points)
        uint64 lastUpdated;             // Timestamp
        uint8 dataQuality;              // 0-2
    }

    /**
     * @notice Score attestation with optimistic oracle data
     * @param scoreHash Hash of the score data
     * @param merkleRoot Merkle root of supporting evidence
     * @param attester Address that posted the attestation
     * @param timestamp When attestation was posted
     * @param challengeDeadline Deadline for challenges
     * @param challenged Whether this attestation has been challenged
     */
    struct ScoreAttestation {
        bytes32 scoreHash;
        bytes32 merkleRoot;
        address attester;
        uint64 timestamp;
        uint64 challengeDeadline;
        bool challenged;
        bool finalized;
    }

    /**
     * @notice Challenge to a score attestation
     * @param challenger Address that initiated the challenge
     * @param bond Amount of bond posted
     * @param reason Brief reason code for challenge
     * @param resolved Whether challenge has been resolved
     */
    struct Challenge {
        address challenger;
        uint256 bond;
        uint8 reason;
        bool resolved;
        bool successful;
    }

    // ============ State Variables ============

    /// @notice Current credit scores for each user
    mapping(address => CreditScore) public scores;

    /// @notice Pending attestations (userAddress => attestation)
    mapping(address => ScoreAttestation) public pendingAttestations;

    /// @notice Challenges (userAddress => challenge)
    mapping(address => Challenge) public challenges;

    /// @notice Authorized score attesters
    mapping(address => bool) public authorizedAttesters;

    /// @notice Challenge period duration (default 1 hour)
    uint256 public challengePeriod = 1 hours;

    /// @notice Minimum challenge bond (default 500 USDC = 500e6)
    uint256 public challengeBond = 500e6;

    /// @notice Challenge reward percentage (default 50% of bond)
    uint256 public challengeRewardBps = 5000; // 50%

    /// @notice Treasury address for failed challenge bonds
    address public treasury;

    // ============ Events ============

    event ScoreAttested(
        address indexed user,
        uint16 score,
        uint8 tier,
        bytes32 merkleRoot,
        address indexed attester,
        uint64 challengeDeadline
    );

    event ScoreChallenged(
        address indexed user,
        address indexed challenger,
        uint256 bond,
        uint8 reason
    );

    event ChallengeResolved(
        address indexed user,
        address indexed challenger,
        bool successful,
        uint256 reward
    );

    event ScoreFinalized(
        address indexed user,
        uint16 score,
        uint8 tier,
        uint8 ltv
    );

    event AttesterAuthorized(address indexed attester, bool authorized);
    event ChallengePeriodUpdated(uint256 newPeriod);
    event ChallengeBondUpdated(uint256 newBond);

    // ============ Errors ============

    error UnauthorizedAttester();
    error InvalidScore();
    error InvalidTier();
    error InvalidLTV();
    error AttestationPending();
    error NoPendingAttestation();
    error ChallengePeriodNotExpired();
    error AlreadyChallenged();
    error InsufficientBond();
    error ChallengeNotFound();
    error AlreadyResolved();
    error InvalidMerkleProof();

    // ============ Constructor ============

    constructor(address _treasury) Ownable(msg.sender) {
        require(_treasury != address(0), "Invalid treasury");
        treasury = _treasury;

        // Initially authorize deployer as attester
        authorizedAttesters[msg.sender] = true;
        emit AttesterAuthorized(msg.sender, true);
    }

    // ============ External Functions ============

    /**
     * @notice Attest to a user's credit score (optimistic oracle)
     * @param user User address
     * @param score Credit score (300-850)
     * @param tier Credit tier (0-4)
     * @param ltv Recommended LTV (0-90)
     * @param interestRateMultiplier Interest rate multiplier (80-150)
     * @param dataQuality Data quality (0-2)
     * @param merkleRoot Merkle root of supporting evidence
     */
    function attestScore(
        address user,
        uint16 score,
        uint8 tier,
        uint8 ltv,
        uint16 interestRateMultiplier,
        uint8 dataQuality,
        bytes32 merkleRoot
    ) external {
        if (!authorizedAttesters[msg.sender]) revert UnauthorizedAttester();
        if (score < 300 || score > 850) revert InvalidScore();
        if (tier > 4) revert InvalidTier();
        if (ltv > 90) revert InvalidLTV();
        if (pendingAttestations[user].attester != address(0) && !pendingAttestations[user].finalized) {
            revert AttestationPending();
        }

        // Create score hash
        bytes32 scoreHash = keccak256(abi.encode(
            user,
            score,
            tier,
            ltv,
            interestRateMultiplier,
            dataQuality,
            block.timestamp
        ));

        // Create attestation
        uint64 deadline = uint64(block.timestamp + challengePeriod);
        pendingAttestations[user] = ScoreAttestation({
            scoreHash: scoreHash,
            merkleRoot: merkleRoot,
            attester: msg.sender,
            timestamp: uint64(block.timestamp),
            challengeDeadline: deadline,
            challenged: false,
            finalized: false
        });

        emit ScoreAttested(user, score, tier, merkleRoot, msg.sender, deadline);
    }

    /**
     * @notice Challenge a pending score attestation
     * @param user User whose score is being challenged
     * @param reason Reason code for challenge
     */
    function challengeScore(address user, uint8 reason) external payable {
        ScoreAttestation storage attestation = pendingAttestations[user];
        if (attestation.attester == address(0)) revert NoPendingAttestation();
        if (attestation.challenged) revert AlreadyChallenged();
        if (block.timestamp >= attestation.challengeDeadline) revert ChallengePeriodNotExpired();
        if (msg.value < challengeBond) revert InsufficientBond();

        // Mark as challenged
        attestation.challenged = true;

        // Create challenge record
        challenges[user] = Challenge({
            challenger: msg.sender,
            bond: msg.value,
            reason: reason,
            resolved: false,
            successful: false
        });

        emit ScoreChallenged(user, msg.sender, msg.value, reason);
    }

    /**
     * @notice Resolve a challenge by providing merkle proof
     * @param user User address
     * @param score Credit score being attested
     * @param tier Credit tier
     * @param ltv LTV
     * @param interestRateMultiplier Interest rate multiplier
     * @param dataQuality Data quality
     * @param proof Merkle proof of evidence
     */
    function resolveChallenge(
        address user,
        uint16 score,
        uint8 tier,
        uint8 ltv,
        uint16 interestRateMultiplier,
        uint8 dataQuality,
        bytes32[] calldata proof
    ) external {
        ScoreAttestation storage attestation = pendingAttestations[user];
        Challenge storage challenge = challenges[user];

        if (!attestation.challenged) revert ChallengeNotFound();
        if (challenge.resolved) revert AlreadyResolved();

        // Verify score hash matches
        bytes32 scoreHash = keccak256(abi.encode(
            user,
            score,
            tier,
            ltv,
            interestRateMultiplier,
            dataQuality,
            attestation.timestamp
        ));

        require(scoreHash == attestation.scoreHash, "Score hash mismatch");

        // Verify merkle proof (simplified - in production, would verify full evidence)
        bytes32 leaf = keccak256(abi.encodePacked(user, score));
        bool validProof = MerkleProof.verify(proof, attestation.merkleRoot, leaf);

        challenge.resolved = true;

        if (validProof) {
            // Challenge failed - attester was correct
            challenge.successful = false;

            // Return bond to challenger minus penalty
            uint256 penalty = (challenge.bond * challengeRewardBps) / 10000;
            payable(challenge.challenger).transfer(challenge.bond - penalty);
            payable(treasury).transfer(penalty);

            // Finalize score
            _finalizeScore(user, score, tier, ltv, interestRateMultiplier, dataQuality);

            emit ChallengeResolved(user, challenge.challenger, false, 0);
        } else {
            // Challenge succeeded - attester was wrong
            challenge.successful = true;

            // Reward challenger
            uint256 reward = challenge.bond + (challenge.bond * challengeRewardBps) / 10000;
            payable(challenge.challenger).transfer(reward);

            // Clear invalid attestation
            delete pendingAttestations[user];

            emit ChallengeResolved(user, challenge.challenger, true, reward);
        }
    }

    /**
     * @notice Finalize a score after challenge period expires
     * @param user User address
     * @param score Credit score
     * @param tier Credit tier
     * @param ltv LTV
     * @param interestRateMultiplier Interest rate multiplier
     * @param dataQuality Data quality
     */
    function finalizeScore(
        address user,
        uint16 score,
        uint8 tier,
        uint8 ltv,
        uint16 interestRateMultiplier,
        uint8 dataQuality
    ) external {
        ScoreAttestation storage attestation = pendingAttestations[user];

        if (attestation.attester == address(0)) revert NoPendingAttestation();
        if (block.timestamp < attestation.challengeDeadline) revert ChallengePeriodNotExpired();
        if (attestation.challenged) revert AlreadyChallenged();

        // Verify score hash
        bytes32 scoreHash = keccak256(abi.encode(
            user,
            score,
            tier,
            ltv,
            interestRateMultiplier,
            dataQuality,
            attestation.timestamp
        ));

        require(scoreHash == attestation.scoreHash, "Score hash mismatch");

        _finalizeScore(user, score, tier, ltv, interestRateMultiplier, dataQuality);
    }

    /**
     * @notice Get user's current credit score
     * @param user User address
     * @return CreditScore struct
     */
    function getScore(address user) external view returns (CreditScore memory) {
        return scores[user];
    }

    /**
     * @notice Check if user has a valid, recent score
     * @param user User address
     * @param maxAge Maximum age in seconds (e.g., 30 days)
     * @return bool True if score is valid and recent
     */
    function hasValidScore(address user, uint256 maxAge) external view returns (bool) {
        CreditScore memory userScore = scores[user];
        if (userScore.score == 0) return false;
        if (block.timestamp - userScore.lastUpdated > maxAge) return false;
        return true;
    }

    // ============ Admin Functions ============

    /**
     * @notice Authorize/deauthorize a score attester
     * @param attester Attester address
     * @param authorized Whether to authorize
     */
    function setAuthorizedAttester(address attester, bool authorized) external onlyOwner {
        authorizedAttesters[attester] = authorized;
        emit AttesterAuthorized(attester, authorized);
    }

    /**
     * @notice Update challenge period
     * @param newPeriod New period in seconds
     */
    function setChallengePeriod(uint256 newPeriod) external onlyOwner {
        require(newPeriod >= 10 minutes && newPeriod <= 24 hours, "Invalid period");
        challengePeriod = newPeriod;
        emit ChallengePeriodUpdated(newPeriod);
    }

    /**
     * @notice Update challenge bond amount
     * @param newBond New bond amount
     */
    function setChallengeBond(uint256 newBond) external onlyOwner {
        require(newBond >= 100e6 && newBond <= 10000e6, "Invalid bond"); // 100-10,000 USDC
        challengeBond = newBond;
        emit ChallengeBondUpdated(newBond);
    }

    /**
     * @notice Update treasury address
     * @param newTreasury New treasury address
     */
    function setTreasury(address newTreasury) external onlyOwner {
        require(newTreasury != address(0), "Invalid treasury");
        treasury = newTreasury;
    }

    // ============ Internal Functions ============

    /**
     * @notice Internal function to finalize a credit score
     */
    function _finalizeScore(
        address user,
        uint16 score,
        uint8 tier,
        uint8 ltv,
        uint16 interestRateMultiplier,
        uint8 dataQuality
    ) internal {
        scores[user] = CreditScore({
            score: score,
            tier: tier,
            ltv: ltv,
            interestRateMultiplier: interestRateMultiplier,
            lastUpdated: uint64(block.timestamp),
            dataQuality: dataQuality
        });

        pendingAttestations[user].finalized = true;

        emit ScoreFinalized(user, score, tier, ltv);
    }

    /**
     * @notice Emergency withdrawal (only for stuck funds)
     */
    function emergencyWithdraw() external onlyOwner {
        payable(treasury).transfer(address(this).balance);
    }

    // Allow contract to receive ETH for challenges
    receive() external payable {}
}

contracts/DutchAuctionLiquidator.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IReputationScorer {
    function scores(address user) external view returns (
        uint256 baseScore,
        uint256 paymentScore,
        uint256 walletAgeScore,
        uint256 protocolScore,
        uint256 totalScore,
        uint256 lastUpdated
    );
}

interface ILendingPool {
    function getLoan(uint256 loanId) external view returns (
        address borrower,
        uint256 collateralAmount,
        uint256 borrowedAmount,
        uint256 interestRate,
        uint256 startTime,
        uint256 duration,
        bool active
    );

    function liquidateLoan(uint256 loanId, address liquidator) external;
}

/**
 * @title DutchAuctionLiquidator
 * @notice Dutch auction liquidation system with reputation-based grace periods
 * @dev Discount increases linearly from 0% to 20% over 6 hours
 */
contract DutchAuctionLiquidator is Ownable, ReentrancyGuard {

    // ============ Structs ============

    struct LiquidationAuction {
        uint256 loanId;
        address borrower;
        uint256 debtAmount;
        uint256 collateralAmount;
        uint256 startTime;
        uint256 gracePeriodEnd;
        uint256 auctionDuration;    // 6 hours in seconds
        uint256 maxDiscount;        // 20% (2000 basis points)
        bool executed;
        address executor;
        uint256 executedAt;
    }

    // ============ State Variables ============

    IReputationScorer public reputationScorer;
    ILendingPool public lendingPool;

    mapping(uint256 => LiquidationAuction) public auctions;
    uint256 public auctionCounter;

    // Grace periods based on reputation (in hours)
    uint256 public constant PLATINUM_GRACE_PERIOD = 72 hours;  // 3 days
    uint256 public constant GOLD_GRACE_PERIOD = 24 hours;      // 1 day
    uint256 public constant SILVER_GRACE_PERIOD = 0;           // Immediate
    uint256 public constant BRONZE_GRACE_PERIOD = 0;           // Immediate

    // Auction parameters
    uint256 public constant AUCTION_DURATION = 6 hours;
    uint256 public constant MAX_DISCOUNT_BPS = 2000; // 20%
    uint256 public constant BASIS_POINTS = 10000;

    // ============ Events ============

    event AuctionStarted(
        uint256 indexed auctionId,
        uint256 indexed loanId,
        address indexed borrower,
        uint256 debtAmount,
        uint256 collateralAmount,
        uint256 gracePeriodEnd
    );

    event AuctionExecuted(
        uint256 indexed auctionId,
        uint256 indexed loanId,
        address indexed executor,
        uint256 discountBps,
        uint256 collateralReceived
    );

    event AuctionCancelled(uint256 indexed auctionId, string reason);

    // ============ Constructor ============

    constructor(
        address _reputationScorer,
        address _lendingPool
    ) Ownable(msg.sender) {
        reputationScorer = IReputationScorer(_reputationScorer);
        lendingPool = ILendingPool(_lendingPool);
    }

    // ============ External Functions ============

    /**
     * @notice Start a liquidation auction for an unhealthy loan
     * @param loanId ID of the loan to liquidate
     * @return auctionId The ID of the created auction
     */
    function startLiquidation(uint256 loanId) external returns (uint256) {
        (
            address borrower,
            uint256 collateralAmount,
            uint256 borrowedAmount,
            ,
            ,
            ,
            bool active
        ) = lendingPool.getLoan(loanId);

        require(active, "Loan not active");
        require(borrower != address(0), "Invalid loan");

        // Calculate grace period based on reputation
        uint256 gracePeriod = getGracePeriod(borrower);
        uint256 gracePeriodEnd = block.timestamp + gracePeriod;

        // Create auction
        uint256 auctionId = auctionCounter++;

        auctions[auctionId] = LiquidationAuction({
            loanId: loanId,
            borrower: borrower,
            debtAmount: borrowedAmount,
            collateralAmount: collateralAmount,
            startTime: block.timestamp,
            gracePeriodEnd: gracePeriodEnd,
            auctionDuration: AUCTION_DURATION,
            maxDiscount: MAX_DISCOUNT_BPS,
            executed: false,
            executor: address(0),
            executedAt: 0
        });

        emit AuctionStarted(
            auctionId,
            loanId,
            borrower,
            borrowedAmount,
            collateralAmount,
            gracePeriodEnd
        );

        return auctionId;
    }

    /**
     * @notice Execute a liquidation auction
     * @param auctionId ID of the auction to execute
     */
    function executeLiquidation(uint256 auctionId) external nonReentrant {
        LiquidationAuction storage auction = auctions[auctionId];

        require(!auction.executed, "Auction already executed");
        require(block.timestamp >= auction.gracePeriodEnd, "Grace period active");

        // Calculate current discount
        uint256 discountBps = getCurrentDiscount(auctionId);

        // Calculate collateral to receive
        uint256 collateralValue = auction.collateralAmount;
        uint256 discountAmount = (collateralValue * discountBps) / BASIS_POINTS;
        uint256 collateralAfterDiscount = collateralValue - discountAmount;

        require(
            collateralAfterDiscount >= auction.debtAmount,
            "Insufficient collateral after discount"
        );

        // Mark as executed
        auction.executed = true;
        auction.executor = msg.sender;
        auction.executedAt = block.timestamp;

        // Execute liquidation through lending pool
        lendingPool.liquidateLoan(auction.loanId, msg.sender);

        emit AuctionExecuted(
            auctionId,
            auction.loanId,
            msg.sender,
            discountBps,
            collateralAfterDiscount
        );
    }

    /**
     * @notice Cancel an auction (e.g., if loan was repaid during grace period)
     * @param auctionId ID of the auction to cancel
     */
    function cancelAuction(uint256 auctionId, string calldata reason) external onlyOwner {
        LiquidationAuction storage auction = auctions[auctionId];

        require(!auction.executed, "Auction already executed");

        auction.executed = true;

        emit AuctionCancelled(auctionId, reason);
    }

    // ============ View Functions ============

    /**
     * @notice Get current discount for an auction
     * @param auctionId ID of the auction
     * @return Discount in basis points (0-2000)
     */
    function getCurrentDiscount(uint256 auctionId) public view returns (uint256) {
        LiquidationAuction memory auction = auctions[auctionId];

        if (block.timestamp < auction.gracePeriodEnd) {
            return 0; // No discount during grace period
        }

        uint256 auctionStart = auction.gracePeriodEnd;
        uint256 auctionEnd = auctionStart + auction.auctionDuration;

        if (block.timestamp >= auctionEnd) {
            return auction.maxDiscount; // Max discount after auction ends
        }

        // Linear interpolation: 0% → 20% over 6 hours
        uint256 elapsed = block.timestamp - auctionStart;
        uint256 discountBps = (elapsed * auction.maxDiscount) / auction.auctionDuration;

        return discountBps;
    }

    /**
     * @notice Get grace period for a borrower based on reputation
     * @param borrower Address of the borrower
     * @return Grace period in seconds
     */
    function getGracePeriod(address borrower) public view returns (uint256) {
        (, , , , uint256 totalScore, ) = reputationScorer.scores(borrower);

        if (totalScore >= 800) return PLATINUM_GRACE_PERIOD; // 72 hours
        if (totalScore >= 600) return GOLD_GRACE_PERIOD;     // 24 hours
        if (totalScore >= 400) return SILVER_GRACE_PERIOD;   // 0 hours
        return BRONZE_GRACE_PERIOD;                          // 0 hours
    }

    /**
     * @notice Get auction details
     * @param auctionId ID of the auction
     * @return Auction struct
     */
    function getAuction(uint256 auctionId) external view returns (LiquidationAuction memory) {
        return auctions[auctionId];
    }

    /**
     * @notice Check if auction is executable
     * @param auctionId ID of the auction
     * @return Whether the auction can be executed
     */
    function isExecutable(uint256 auctionId) external view returns (bool) {
        LiquidationAuction memory auction = auctions[auctionId];

        if (auction.executed) return false;
        if (block.timestamp < auction.gracePeriodEnd) return false;

        return true;
    }

    /**
     * @notice Get time remaining in grace period
     * @param auctionId ID of the auction
     * @return Seconds remaining (0 if grace period ended)
     */
    function getGracePeriodRemaining(uint256 auctionId) external view returns (uint256) {
        LiquidationAuction memory auction = auctions[auctionId];

        if (block.timestamp >= auction.gracePeriodEnd) {
            return 0;
        }

        return auction.gracePeriodEnd - block.timestamp;
    }

    // ============ Admin Functions ============

    /**
     * @notice Update reputation scorer address
     * @param _reputationScorer New reputation scorer address
     */
    function setReputationScorer(address _reputationScorer) external onlyOwner {
        reputationScorer = IReputationScorer(_reputationScorer);
    }

    /**
     * @notice Update lending pool address
     * @param _lendingPool New lending pool address
     */
    function setLendingPool(address _lendingPool) external onlyOwner {
        lendingPool = ILendingPool(_lendingPool);
    }
}

contracts/HealthFactorMonitor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";

interface IReputationScorer {
    function getDynamicLTV(address user) external view returns (uint256);
}

interface ILendingPool {
    function getLoan(uint256 loanId) external view returns (
        address borrower,
        uint256 collateralAmount,
        uint256 borrowedAmount,
        uint256 interestRate,
        uint256 startTime,
        uint256 duration,
        bool active
    );
}

interface IPriceOracle {
    function getPrice(address token) external view returns (uint256);
}

/**
 * @title HealthFactorMonitor
 * @notice Real-time health factor monitoring for all active loans
 * @dev Health Factor = (Collateral Value × Dynamic LTV) / Debt Value
 *      Liquidation threshold: HF < 0.95
 */
contract HealthFactorMonitor is Ownable {

    // ============ Structs ============

    struct HealthStatus {
        uint256 collateralValue;    // In USD (18 decimals)
        uint256 debtValue;          // In USD (18 decimals)
        uint256 dynamicLTV;         // Percentage (50-90)
        uint256 healthFactor;       // Scaled by 1e18
        bool liquidatable;          // True if HF <= 0.95
        uint256 lastUpdate;         // Timestamp of last update
    }

    // ============ State Variables ============

    IReputationScorer public reputationScorer;
    ILendingPool public lendingPool;
    IPriceOracle public priceOracle;

    // borrower => loanId => HealthStatus
    mapping(address => mapping(uint256 => HealthStatus)) public healthStatuses;

    // Liquidation threshold (95% = 0.95e18)
    uint256 public LIQUIDATION_THRESHOLD = 0.95e18;
    uint256 public constant PRECISION = 1e18;

    // ============ Events ============

    event HealthFactorUpdated(
        address indexed borrower,
        uint256 indexed loanId,
        uint256 healthFactor,
        bool liquidatable
    );

    event HealthFactorCritical(
        address indexed borrower,
        uint256 indexed loanId,
        uint256 healthFactor
    );

    event OracleUpdated(address indexed newOracle);
    event ThresholdUpdated(uint256 newThreshold);

    // ============ Constructor ============

    constructor(
        address _reputationScorer,
        address _lendingPool,
        address _priceOracle
    ) Ownable(msg.sender) {
        reputationScorer = IReputationScorer(_reputationScorer);
        lendingPool = ILendingPool(_lendingPool);
        priceOracle = IPriceOracle(_priceOracle);
    }

    // ============ External Functions ============

    /**
     * @notice Calculate health factor for a loan
     * @param borrower Address of the borrower
     * @param loanId ID of the loan
     * @return Health factor scaled by 1e18
     */
    function calculateHealthFactor(
        address borrower,
        uint256 loanId
    ) external returns (uint256) {
        (
            ,
            uint256 collateralAmount,
            uint256 borrowedAmount,
            ,
            ,
            ,
            bool active
        ) = lendingPool.getLoan(loanId);

        require(active, "Loan not active");

        // Get dynamic LTV based on reputation
        uint256 dynamicLTV = reputationScorer.getDynamicLTV(borrower);

        // Get collateral value from price oracle
        // For now, assuming ETH collateral - will need token address parameter
        uint256 collateralValue = collateralAmount; // TODO: Multiply by price

        // Calculate debt value (borrowed amount + accrued interest)
        uint256 debtValue = borrowedAmount; // TODO: Add accrued interest

        // Calculate health factor
        // HF = (collateralValue × LTV%) / debtValue
        uint256 healthFactor;
        if (debtValue == 0) {
            healthFactor = type(uint256).max; // Infinite HF when no debt
        } else if (collateralAmount == 0) {
            healthFactor = 0; // Zero HF when no collateral
        } else {
            healthFactor = (collateralValue * dynamicLTV * PRECISION) / (debtValue * 100);
        }

        // Check if liquidatable (HF <= threshold, not <)
        bool liquidatable = healthFactor <= LIQUIDATION_THRESHOLD;

        // Store health status
        healthStatuses[borrower][loanId] = HealthStatus({
            collateralValue: collateralValue,
            debtValue: debtValue,
            dynamicLTV: dynamicLTV,
            healthFactor: healthFactor,
            liquidatable: liquidatable,
            lastUpdate: block.timestamp
        });

        emit HealthFactorUpdated(borrower, loanId, healthFactor, liquidatable);

        if (liquidatable) {
            emit HealthFactorCritical(borrower, loanId, healthFactor);
        }

        return healthFactor;
    }

    /**
     * @notice Check if a loan is liquidatable
     * @param borrower Address of the borrower
     * @param loanId ID of the loan
     * @return Whether the loan can be liquidated
     */
    function isLiquidatable(
        address borrower,
        uint256 loanId
    ) external view returns (bool) {
        HealthStatus memory status = healthStatuses[borrower][loanId];

        // If never calculated, return false
        if (status.lastUpdate == 0) {
            return false;
        }

        return status.healthFactor <= LIQUIDATION_THRESHOLD;
    }

    /**
     * @notice Get health status for a loan
     * @param borrower Address of the borrower
     * @param loanId ID of the loan
     * @return HealthStatus struct
     */
    function getHealthStatus(
        address borrower,
        uint256 loanId
    ) external view returns (HealthStatus memory) {
        return healthStatuses[borrower][loanId];
    }

    /**
     * @notice Batch calculate health factors for multiple loans
     * @param borrowers Array of borrower addresses
     * @param loanIds Array of loan IDs
     */
    function batchCalculateHealthFactors(
        address[] calldata borrowers,
        uint256[] calldata loanIds
    ) external {
        require(borrowers.length == loanIds.length, "Array length mismatch");

        for (uint256 i = 0; i < borrowers.length; i++) {
            try this.calculateHealthFactor(borrowers[i], loanIds[i]) {
                // Success
            } catch {
                // Skip failed calculations
                continue;
            }
        }
    }

    /**
     * @notice Get liquidation risk level
     * @param borrower Address of the borrower
     * @param loanId ID of the loan
     * @return Risk level: 0 (Safe), 1 (Warning), 2 (Danger), 3 (Critical)
     */
    function getRiskLevel(
        address borrower,
        uint256 loanId
    ) external view returns (uint8) {
        HealthStatus memory status = healthStatuses[borrower][loanId];

        if (status.healthFactor >= 1.2e18) {
            return 0; // Safe (HF >= 1.2)
        } else if (status.healthFactor >= 1.05e18) {
            return 1; // Warning (1.05 <= HF < 1.2)
        } else if (status.healthFactor > LIQUIDATION_THRESHOLD) {
            return 2; // Danger (0.95 < HF < 1.05)
        } else {
            return 3; // Critical (HF <= 0.95 - Liquidatable)
        }
    }

    /**
     * @notice Calculate required collateral to reach target health factor
     * @param borrower Address of the borrower
     * @param loanId ID of the loan
     * @param targetHF Target health factor (e.g., 1.5e18 for 1.5)
     * @return Additional collateral needed
     */
    function getRequiredCollateral(
        address borrower,
        uint256 loanId,
        uint256 targetHF
    ) external view returns (uint256) {
        HealthStatus memory status = healthStatuses[borrower][loanId];

        if (status.healthFactor >= targetHF) {
            return 0; // Already above target
        }

        // Required collateral = (targetHF × debtValue × 100) / (LTV × PRECISION)
        uint256 requiredCollateral = (targetHF * status.debtValue * 100) / (status.dynamicLTV * PRECISION);
        uint256 additionalCollateral = requiredCollateral > status.collateralValue
            ? requiredCollateral - status.collateralValue
            : 0;

        return additionalCollateral;
    }

    // ============ Admin Functions ============

    /**
     * @notice Update reputation scorer address
     * @param _reputationScorer New reputation scorer address
     */
    function setReputationScorer(address _reputationScorer) external onlyOwner {
        reputationScorer = IReputationScorer(_reputationScorer);
    }

    /**
     * @notice Update lending pool address
     * @param _lendingPool New lending pool address
     */
    function setLendingPool(address _lendingPool) external onlyOwner {
        lendingPool = ILendingPool(_lendingPool);
    }

    /**
     * @notice Update price oracle address
     * @param _priceOracle New price oracle address
     */
    function setPriceOracle(address _priceOracle) external onlyOwner {
        priceOracle = IPriceOracle(_priceOracle);
    }

    /**
     * @notice Update oracle address (alias for setPriceOracle)
     * @param newOracle New oracle address
     */
    function updateOracle(address newOracle) external onlyOwner {
        priceOracle = IPriceOracle(newOracle);
        emit OracleUpdated(newOracle);
    }

    /**
     * @notice Update liquidation threshold
     * @param newThreshold New threshold (e.g., 0.9e18 for 90%)
     */
    function updateLiquidationThreshold(uint256 newThreshold) external onlyOwner {
        require(newThreshold > 0 && newThreshold <= 1e18, "Invalid threshold");
        LIQUIDATION_THRESHOLD = newThreshold;
        emit ThresholdUpdated(newThreshold);
    }
}

contracts/InsuranceFund.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title InsuranceFund
 * @notice Protocol insurance fund for bad debt coverage
 * @dev Collects 5% of protocol revenue and provides first-loss protection
 */
contract InsuranceFund is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    // ============ Structs ============

    struct CoverageRecord {
        uint256 loanId;
        address lender;
        uint256 lossAmount;
        uint256 coveredAmount;
        uint256 timestamp;
    }

    // ============ State Variables ============

    IERC20 public immutable stablecoin; // USDC/USDT

    uint256 public totalFunds;
    uint256 public totalCovered;
    uint256 public totalDefaults;

    // Revenue allocation percentage (5% = 500 basis points)
    uint256 public constant REVENUE_ALLOCATION_PERCENT = 500; // 5% in basis points
    uint256 public constant REVENUE_PERCENTAGE = 5;
    uint256 public constant PERCENTAGE_DENOMINATOR = 100;

    // Maximum coverage per loan (0.25% = 25 basis points)
    uint256 public constant MAX_COVERAGE_PERCENT = 25; // 0.25% in basis points
    uint256 public constant MAX_COVERAGE_BPS = 25;
    uint256 public constant BASIS_POINTS = 10000;

    // Authorized contracts that can request coverage
    mapping(address => bool) public authorizedRequestors;

    // Lender contributions (optional)
    mapping(address => uint256) public lenderContributions;

    // Coverage history
    CoverageRecord[] public coverageHistory;

    // ============ Events ============

    event Deposit(address indexed depositor, uint256 amount);
    event FundsDeposited(address indexed depositor, uint256 amount, uint256 newTotal);
    event RevenueAllocated(uint256 protocolRevenue, uint256 allocationAmount);
    event LossCovered(
        uint256 indexed loanId,
        address indexed lender,
        uint256 lossAmount,
        uint256 coveredAmount
    );
    event AuthorizedRequestorSet(address indexed requestor, bool authorized);
    event EmergencyWithdrawal(address indexed recipient, uint256 amount);

    // ============ Constructor ============

    constructor(address _stablecoin) Ownable(msg.sender) {
        stablecoin = IERC20(_stablecoin);
    }

    // ============ External Functions ============

    /**
     * @notice Deposit funds into the insurance pool
     * @param amount Amount to deposit
     */
    function deposit(uint256 amount) external nonReentrant {
        require(amount > 0, "Amount must be > 0");

        stablecoin.safeTransferFrom(msg.sender, address(this), amount);

        totalFunds += amount;
        lenderContributions[msg.sender] += amount;

        emit Deposit(msg.sender, amount);
        emit FundsDeposited(msg.sender, amount, totalFunds);
    }

    /**
     * @notice Allocate protocol revenue to insurance fund
     * @param protocolRevenue Total protocol revenue from fees
     */
    function allocateRevenue(uint256 protocolRevenue) external onlyAuthorized nonReentrant {
        require(protocolRevenue > 0, "Amount must be > 0");

        uint256 allocationAmount = (protocolRevenue * REVENUE_PERCENTAGE) / PERCENTAGE_DENOMINATOR;

        stablecoin.safeTransferFrom(msg.sender, address(this), allocationAmount);

        totalFunds += allocationAmount;

        emit RevenueAllocated(protocolRevenue, allocationAmount);
    }

    /**
     * @notice Cover a loss from a defaulted loan
     * @param lender Address of the lender who suffered the loss
     * @param loanId ID of the defaulted loan (for record keeping)
     * @param principal Original loan principal amount
     * @param lossAmount Total loss amount
     * @return Amount actually covered by insurance
     */
    function coverLoss(
        address lender,
        uint256 loanId,
        uint256 principal,
        uint256 lossAmount
    ) external onlyAuthorized nonReentrant returns (uint256) {
        require(lender != address(0), "Invalid lender address");
        require(lossAmount > 0, "Loss amount must be > 0");

        // Calculate maximum coverage (0.25% of principal)
        uint256 maxCoverage = (principal * MAX_COVERAGE_BPS) / BASIS_POINTS;

        // Coverage is lesser of: available funds, max coverage, or actual loss
        uint256 coveredAmount = _min(_min(totalFunds, maxCoverage), lossAmount);

        require(coveredAmount <= totalFunds, "Insufficient funds");

        if (coveredAmount > 0) {
            totalFunds -= coveredAmount;
            totalCovered += coveredAmount;
            totalDefaults++;

            // Transfer coverage to lender
            stablecoin.safeTransfer(lender, coveredAmount);

            // Record coverage
            coverageHistory.push(CoverageRecord({
                loanId: loanId,
                lender: lender,
                lossAmount: lossAmount,
                coveredAmount: coveredAmount,
                timestamp: block.timestamp
            }));

            emit LossCovered(loanId, lender, lossAmount, coveredAmount);
        }

        return coveredAmount;
    }

    // ============ View Functions ============

    /**
     * @notice Get maximum possible coverage for a principal amount
     * @dev Always returns 0.25% of principal, regardless of fund balance
     * @param principal Loan principal amount
     * @return Maximum coverage (0.25% of principal)
     */
    function getMaxCoveragePercent(uint256 principal) public pure returns (uint256) {
        return (principal * MAX_COVERAGE_BPS) / BASIS_POINTS;
    }

    /**
     * @notice Get available coverage for a given principal
     * @dev Limited by current fund balance
     * @param principal Loan principal amount
     * @return Coverage amount (min of max coverage and available funds)
     */
    function getAvailableCoverage(uint256 principal) public view returns (uint256) {
        uint256 maxCoverage = (principal * MAX_COVERAGE_BPS) / BASIS_POINTS;
        return _min(totalFunds, maxCoverage);
    }

    /**
     * @notice Get insurance fund statistics
     * @return _totalFunds Total funds available
     * @return _totalCovered Total amount covered historically
     * @return _totalDefaults Number of defaults covered
     * @return _utilizationRate Percentage of funds used (basis points)
     */
    function getStatistics() external view returns (
        uint256 _totalFunds,
        uint256 _totalCovered,
        uint256 _totalDefaults,
        uint256 _utilizationRate
    ) {
        _totalFunds = totalFunds;
        _totalCovered = totalCovered;
        _totalDefaults = totalDefaults;

        uint256 totalHistoricalFunds = totalFunds + totalCovered;
        _utilizationRate = totalHistoricalFunds > 0
            ? (totalCovered * BASIS_POINTS) / totalHistoricalFunds
            : 0;
    }

    /**
     * @notice Get coverage history
     * @param startIndex Starting index
     * @param count Number of records to return
     * @return Array of coverage records
     */
    function getCoverageHistory(
        uint256 startIndex,
        uint256 count
    ) external view returns (CoverageRecord[] memory) {
        require(startIndex < coverageHistory.length, "Invalid start index");

        uint256 endIndex = _min(startIndex + count, coverageHistory.length);
        uint256 resultCount = endIndex - startIndex;

        CoverageRecord[] memory records = new CoverageRecord[](resultCount);

        for (uint256 i = 0; i < resultCount; i++) {
            records[i] = coverageHistory[startIndex + i];
        }

        return records;
    }

    /**
     * @notice Get total coverage history count
     * @return Number of coverage records
     */
    function getCoverageHistoryCount() external view returns (uint256) {
        return coverageHistory.length;
    }

    /**
     * @notice Check if fund can cover a specific loss
     * @param principal Loan principal
     * @return Whether the loss can be covered
     */
    function canCoverLoss(uint256 principal) external view returns (bool) {
        return getAvailableCoverage(principal) > 0;
    }

    /**
     * @notice Get default history for a specific loan
     * @param loanId ID of the loan
     * @return Coverage record for the loan
     */
    function getDefaultHistory(uint256 loanId) external view returns (CoverageRecord memory) {
        for (uint256 i = 0; i < coverageHistory.length; i++) {
            if (coverageHistory[i].loanId == loanId) {
                return coverageHistory[i];
            }
        }
        // Return empty record if not found
        return CoverageRecord({
            loanId: 0,
            lender: address(0),
            lossAmount: 0,
            coveredAmount: 0,
            timestamp: 0
        });
    }

    // ============ Admin Functions ============

    /**
     * @notice Set authorization for a contract to request coverage
     * @param requestor Address of the requestor contract
     * @param authorized Whether the requestor is authorized
     */
    function setAuthorizedRequestor(address requestor, bool authorized) external onlyOwner {
        authorizedRequestors[requestor] = authorized;
        emit AuthorizedRequestorSet(requestor, authorized);
    }

    /**
     * @notice Emergency withdrawal (only owner, for migrations)
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(uint256 amount) external onlyOwner nonReentrant {
        require(amount <= totalFunds, "Insufficient funds");

        totalFunds -= amount;
        stablecoin.safeTransfer(msg.sender, amount);

        emit EmergencyWithdrawal(msg.sender, amount);
    }

    // ============ Internal Functions ============

    function _min(uint256 a, uint256 b) internal pure returns (uint256) {
        return a < b ? a : b;
    }

    // ============ Modifiers ============

    modifier onlyAuthorized() {
        require(
            authorizedRequestors[msg.sender] || msg.sender == owner(),
            "Not authorized"
        );
        _;
    }
}

