contracts/CreditRegistryV3.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/MessageHashUtils.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title CreditRegistryV3
 * @notice Complete credit bureau with S1-S5 data tracking
 * @dev Phase 3B - Full 5-factor credit scoring system
 *
 * ENHANCEMENTS OVER V2:
 * - S2: Collateral utilization tracking
 * - S3: KYC proof storage (Didit integration)
 * - S4: Cross-chain reputation storage (CCIP ready)
 * - S5: Governance participation tracking
 */
contract CreditRegistryV3 is Ownable, ReentrancyGuard {
    using ECDSA for bytes32;
    using MessageHashUtils for bytes32;

    // ==================== EVENTS ====================

    // S1 - Repayment History
    event LoanRegistered(address indexed borrower, uint256 indexed loanId, uint256 principalUsd18, uint256 timestamp, address indexed lender);
    event RepaymentRegistered(address indexed borrower, uint256 indexed loanId, uint256 amountUsd18, uint256 timestamp, address indexed payer);
    event LiquidationRegistered(address indexed borrower, uint256 indexed loanId, uint256 amountUsd18, uint256 timestamp, address indexed liquidator);

    // S2 - Collateral Utilization
    event CollateralDataRecorded(uint256 indexed loanId, address indexed collateralToken, uint256 collateralValueUsd18, uint16 userScore);

    // S3 - Sybil Resistance
    event KYCVerified(address indexed user, bytes32 credentialHash, uint256 expiresAt);
    event StakeDeposited(address indexed staker, uint256 amount, uint256 lockUntil);
    event StakeWithdrawn(address indexed staker, uint256 amount);
    event FirstSeenRecorded(address indexed wallet, uint256 timestamp);

    // S4 - Cross-Chain Reputation
    event CrossChainReputationReceived(address indexed user, uint64 indexed sourceChainSelector, uint16 score, uint256 timestamp);

    // S5 - Governance Participation
    event VoteRecorded(address indexed voter, uint256 indexed proposalId, uint256 timestamp);
    event ProposalRecorded(address indexed proposer, uint256 indexed proposalId, uint256 timestamp);

    // System
    event LenderAuthorized(address indexed lender, bool allowed);
    event CCIPSenderAuthorized(address indexed sender, bool allowed);
    event GovernanceAuthorized(address indexed governance, bool allowed);

    // ==================== ENUMS ====================

    enum LoanStatus { Unknown, Active, Repaid, Liquidated }

    // ==================== STRUCTS ====================

    // S1 - Repayment History
    struct LoanRecord {
        uint256 loanId;
        address borrower;
        uint256 principalUsd18;
        uint256 repaidUsd18;
        uint256 timestamp;
        LoanStatus status;
        address lender;
    }

    // S2 - Collateral Utilization
    struct CollateralData {
        address collateralToken;
        uint256 collateralValueUsd18;
        uint256 principalUsd18;
        uint16 userScoreAtBorrow;
    }

    // S3 - Sybil Resistance
    struct KYCProof {
        bytes32 credentialHash;
        uint256 verifiedAt;
        uint256 expiresAt;
    }

    struct StakeInfo {
        uint256 amount;
        uint256 lockUntil;
    }

    // S4 - Cross-Chain Reputation
    struct CrossChainScore {
        uint16 overallScore;
        uint256 totalLoans;
        uint256 repaidLoans;
        uint256 updatedAt;
    }

    // S5 - Governance Participation
    struct GovernanceActivity {
        uint256 voteCount;
        uint256 proposalCount;
        uint256 lastVoteTimestamp;
        uint256 lastProposalTimestamp;
    }

    // ==================== STATE ====================

    // S1 - Repayment History
    mapping(uint256 => LoanRecord) private _loansById;
    mapping(address => uint256[]) private _loanIdsByBorrower;
    uint256 private _nextLoanId = 1;

    // S2 - Collateral Utilization
    mapping(uint256 => CollateralData) public loanCollateralData;
    mapping(address => address[]) private _userCollateralAssets; // User => unique collateral tokens

    // S3 - Sybil Resistance
    mapping(address => uint256) public walletFirstSeen;
    mapping(address => KYCProof) public kycProofs;
    mapping(address => StakeInfo) public stakes;
    IERC20 public stakingToken;
    address public kycIssuer; // Didit issuer address

    // S4 - Cross-Chain Reputation
    mapping(address => mapping(uint64 => CrossChainScore)) public crossChainScores; // User => ChainSelector => Score

    // S5 - Governance Participation
    mapping(address => GovernanceActivity) public governanceActivity;

    // Authorization
    mapping(address => bool) public authorizedLenders;
    mapping(address => bool) public authorizedCCIPSenders; // For cross-chain messages
    mapping(address => bool) public authorizedGovernance; // For vote/proposal recording

    // ==================== MODIFIERS ====================

    modifier onlyLender() {
        require(authorizedLenders[msg.sender], "Not authorized lender");
        _;
    }

    modifier onlyCCIPSender() {
        require(authorizedCCIPSenders[msg.sender], "Not authorized CCIP sender");
        _;
    }

    modifier onlyGovernance() {
        require(authorizedGovernance[msg.sender], "Not authorized governance");
        _;
    }

    // ==================== CONSTRUCTOR ====================

    constructor(address _stakingToken, address _kycIssuer) Ownable(msg.sender) {
        stakingToken = IERC20(_stakingToken);
        kycIssuer = _kycIssuer;
    }

    // ==================== S1: REPAYMENT HISTORY ====================

    function registerLoan(address borrower, uint256 principalUsd18) external onlyLender returns (uint256) {
        uint256 loanId = _nextLoanId++;

        _loansById[loanId] = LoanRecord({
            loanId: loanId,
            borrower: borrower,
            principalUsd18: principalUsd18,
            repaidUsd18: 0,
            timestamp: block.timestamp,
            status: LoanStatus.Active,
            lender: msg.sender
        });

        _loanIdsByBorrower[borrower].push(loanId);

        // Record first seen if new user
        if (walletFirstSeen[borrower] == 0) {
            walletFirstSeen[borrower] = block.timestamp;
            emit FirstSeenRecorded(borrower, block.timestamp);
        }

        emit LoanRegistered(borrower, loanId, principalUsd18, block.timestamp, msg.sender);
        return loanId;
    }

    function registerRepayment(uint256 loanId, uint256 amountUsd18) external onlyLender {
        LoanRecord storage loan = _loansById[loanId];
        require(loan.status == LoanStatus.Active, "Loan not active");
        require(loan.lender == msg.sender, "Wrong lender");

        loan.repaidUsd18 += amountUsd18;

        if (loan.repaidUsd18 >= loan.principalUsd18) {
            loan.status = LoanStatus.Repaid;
        }

        emit RepaymentRegistered(loan.borrower, loanId, amountUsd18, block.timestamp, msg.sender);
    }

    function registerLiquidation(uint256 loanId, uint256 recoveredUsd18) external onlyLender {
        LoanRecord storage loan = _loansById[loanId];
        require(loan.status == LoanStatus.Active, "Loan not active");
        require(loan.lender == msg.sender, "Wrong lender");

        loan.status = LoanStatus.Liquidated;
        loan.repaidUsd18 += recoveredUsd18;

        emit LiquidationRegistered(loan.borrower, loanId, recoveredUsd18, block.timestamp, msg.sender);
    }

    // ==================== S2: COLLATERAL UTILIZATION ====================

    function recordCollateralData(
        uint256 loanId,
        address collateralToken,
        uint256 collateralValueUsd18,
        uint16 userScore
    ) external onlyLender {
        require(_loansById[loanId].status == LoanStatus.Active, "Loan not active");

        loanCollateralData[loanId] = CollateralData({
            collateralToken: collateralToken,
            collateralValueUsd18: collateralValueUsd18,
            principalUsd18: _loansById[loanId].principalUsd18,
            userScoreAtBorrow: userScore
        });

        // Track unique collateral assets
        address borrower = _loansById[loanId].borrower;
        if (!_hasCollateralAsset(borrower, collateralToken)) {
            _userCollateralAssets[borrower].push(collateralToken);
        }

        emit CollateralDataRecorded(loanId, collateralToken, collateralValueUsd18, userScore);
    }

    function _hasCollateralAsset(address user, address token) private view returns (bool) {
        address[] memory assets = _userCollateralAssets[user];
        for (uint256 i = 0; i < assets.length; i++) {
            if (assets[i] == token) return true;
        }
        return false;
    }

    // ==================== S3: SYBIL RESISTANCE (KYC) ====================

    function submitKYCProof(
        bytes32 credentialHash,
        uint256 expiresAt,
        bytes memory signature
    ) external {
        // Verify signature from Didit issuer
        bytes32 messageHash = keccak256(abi.encodePacked(msg.sender, credentialHash, expiresAt));
        bytes32 ethSignedHash = messageHash.toEthSignedMessageHash();
        address signer = ethSignedHash.recover(signature);

        require(signer == kycIssuer, "Invalid KYC signature");
        require(expiresAt > block.timestamp, "KYC proof expired");

        kycProofs[msg.sender] = KYCProof({
            credentialHash: credentialHash,
            verifiedAt: block.timestamp,
            expiresAt: expiresAt
        });

        emit KYCVerified(msg.sender, credentialHash, expiresAt);
    }

    function setKYCIssuer(address _kycIssuer) external onlyOwner {
        kycIssuer = _kycIssuer;
    }

    // ==================== S3: SYBIL RESISTANCE (STAKING) ====================

    function stake(uint256 amount) external nonReentrant {
        require(amount > 0, "Zero amount");

        stakingToken.transferFrom(msg.sender, address(this), amount);

        stakes[msg.sender].amount += amount;
        stakes[msg.sender].lockUntil = block.timestamp + 30 days;

        emit StakeDeposited(msg.sender, amount, stakes[msg.sender].lockUntil);
    }

    function unstake(uint256 amount) external nonReentrant {
        require(amount > 0, "Zero amount");
        require(stakes[msg.sender].amount >= amount, "Insufficient stake");
        require(block.timestamp >= stakes[msg.sender].lockUntil, "Stake locked");

        stakes[msg.sender].amount -= amount;
        stakingToken.transfer(msg.sender, amount);

        emit StakeWithdrawn(msg.sender, amount);
    }

    // ==================== S4: CROSS-CHAIN REPUTATION ====================

    function receiveCrossChainReputation(
        address user,
        uint64 sourceChainSelector,
        uint16 overallScore,
        uint256 totalLoans,
        uint256 repaidLoans
    ) external onlyCCIPSender {
        crossChainScores[user][sourceChainSelector] = CrossChainScore({
            overallScore: overallScore,
            totalLoans: totalLoans,
            repaidLoans: repaidLoans,
            updatedAt: block.timestamp
        });

        emit CrossChainReputationReceived(user, sourceChainSelector, overallScore, block.timestamp);
    }

    // ==================== S5: GOVERNANCE PARTICIPATION ====================

    function recordVote(address voter, uint256 proposalId) external onlyGovernance {
        governanceActivity[voter].voteCount++;
        governanceActivity[voter].lastVoteTimestamp = block.timestamp;

        emit VoteRecorded(voter, proposalId, block.timestamp);
    }

    function recordProposal(address proposer, uint256 proposalId) external onlyGovernance {
        governanceActivity[proposer].proposalCount++;
        governanceActivity[proposer].lastProposalTimestamp = block.timestamp;

        emit ProposalRecorded(proposer, proposalId, block.timestamp);
    }

    // ==================== AUTHORIZATION ====================

    function setAuthorizedLender(address lender, bool allowed) external onlyOwner {
        authorizedLenders[lender] = allowed;
        emit LenderAuthorized(lender, allowed);
    }

    function setAuthorizedCCIPSender(address sender, bool allowed) external onlyOwner {
        authorizedCCIPSenders[sender] = allowed;
        emit CCIPSenderAuthorized(sender, allowed);
    }

    function setAuthorizedGovernance(address governance, bool allowed) external onlyOwner {
        authorizedGovernance[governance] = allowed;
        emit GovernanceAuthorized(governance, allowed);
    }

    // ==================== VIEW FUNCTIONS ====================

    // S1 - Repayment History
    function getLoan(uint256 loanId) external view returns (LoanRecord memory) {
        return _loansById[loanId];
    }

    function getLoanIdsByBorrower(address borrower) external view returns (uint256[] memory) {
        return _loanIdsByBorrower[borrower];
    }

    function getFirstSeen(address wallet) external view returns (uint256) {
        return walletFirstSeen[wallet];
    }

    function getStakeInfo(address user) external view returns (StakeInfo memory) {
        return stakes[user];
    }

    // S2 - Collateral Utilization
    function getCollateralData(uint256 loanId) external view returns (CollateralData memory) {
        return loanCollateralData[loanId];
    }

    function getUserCollateralAssets(address user) external view returns (address[] memory) {
        return _userCollateralAssets[user];
    }

    // S3 - Sybil Resistance
    function getKYCProof(address user) external view returns (KYCProof memory) {
        return kycProofs[user];
    }

    function isKYCVerified(address user) external view returns (bool) {
        KYCProof memory proof = kycProofs[user];
        return proof.verifiedAt > 0 && proof.expiresAt > block.timestamp;
    }

    // S4 - Cross-Chain Reputation
    function getCrossChainScore(address user, uint64 chainSelector) external view returns (CrossChainScore memory) {
        return crossChainScores[user][chainSelector];
    }

    // S5 - Governance Participation
    function getGovernanceActivity(address user) external view returns (GovernanceActivity memory) {
        return governanceActivity[user];
    }
}

contracts/CreditVaultV3.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./CreditRegistryV3.sol";
import "./ScoreOraclePhase3B.sol";

/**
 * @title CreditVaultV3
 * @notice Lending vault with complete Phase 3B integration
 * @dev Integrates with 5-factor credit scoring system
 */
contract CreditVaultV3 is Ownable, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    // ==================== STRUCTS ====================

    struct VaultLoanData {
        address collateralToken;
        uint256 collateralAmount;
        uint16 aprBps;
        uint256 startTimestamp;
        uint256 graceStart;
        bool exists;
    }

    struct Asset {
        bool allowed;
        address priceFeed;
    }

    // ==================== STATE ====================

    CreditRegistryV3 public registry;
    ScoreOraclePhase3B public oracle;

    mapping(address => Asset) public assets;
    mapping(uint256 => VaultLoanData) public vaultLoans;
    address public insurancePool;

    // Constants
    uint256 public constant MIN_HEALTH_FACTOR = 1.2e18;
    uint256 public constant LIQUIDATOR_SHARE_BPS = 500; // 5%
    uint256 public constant INSURANCE_SHARE_BPS = 500; // 5%
    uint256 public constant GRACE_BRONZE = 24 hours;
    uint256 public constant GRACE_SILVER = 36 hours;
    uint256 public constant GRACE_GOLD = 48 hours;
    uint256 public constant GRACE_PLATINUM = 72 hours;

    // ==================== EVENTS ====================

    event LoanCreated(uint256 indexed loanId, address indexed borrower, uint256 principalUsd18, uint256 collateralAmount);
    event LoanRepaid(uint256 indexed loanId, address indexed borrower, uint256 amountUsd18);
    event LoanLiquidated(uint256 indexed loanId, address indexed borrower, address indexed liquidator, uint256 collateralAmount);
    event GracePeriodStarted(uint256 indexed loanId, uint256 timestamp);

    // ==================== CONSTRUCTOR ====================

    constructor(
        address payable _registry,
        address _oracle
    ) Ownable(msg.sender) {
        registry = CreditRegistryV3(_registry);
        oracle = ScoreOraclePhase3B(_oracle);
    }

    // ==================== BORROW FUNCTION ====================

    function borrow(
        address collateralToken,
        uint256 collateralAmount,
        uint256 principalUsd18
    ) external nonReentrant whenNotPaused returns (uint256 loanId) {
        require(assets[collateralToken].allowed, "Asset not allowed");
        require(collateralAmount > 0, "Zero collateral");
        require(principalUsd18 > 0, "Zero principal");

        // Get score and max LTV
        ScoreOraclePhase3B.ScoreBreakdown memory score = oracle.computeScore(msg.sender);
        uint16 maxLtvPercent = _maxLtvForScore(score.overall);

        // Check LTV
        uint256 collateralUsd18 = _tokenAmountToUsd18(collateralToken, collateralAmount);
        require(principalUsd18 * 100 <= collateralUsd18 * maxLtvPercent, "Exceeds allowed LTV");

        // Transfer collateral
        IERC20(collateralToken).safeTransferFrom(msg.sender, address(this), collateralAmount);

        // Register loan in registry (get canonical loanId)
        loanId = registry.registerLoan(msg.sender, principalUsd18);

        // Record collateral data in registry (for S2 scoring)
        registry.recordCollateralData(loanId, collateralToken, collateralUsd18, score.overall);

        // Store vault-specific data
        vaultLoans[loanId] = VaultLoanData({
            collateralToken: collateralToken,
            collateralAmount: collateralAmount,
            aprBps: oracle.getAPR(score.overall),
            startTimestamp: block.timestamp,
            graceStart: 0,
            exists: true
        });

        emit LoanCreated(loanId, msg.sender, principalUsd18, collateralAmount);
    }

    // ==================== REPAY FUNCTION ====================

    function repay(uint256 loanId, uint256 amountUsd18) external nonReentrant whenNotPaused {
        VaultLoanData storage vaultData = vaultLoans[loanId];
        require(vaultData.exists, "Loan not found");

        CreditRegistryV3.LoanRecord memory loan = registry.getLoan(loanId);
        require(loan.status == CreditRegistryV3.LoanStatus.Active, "Loan not active");
        require(loan.borrower == msg.sender, "Not borrower");

        // Calculate total debt
        uint256 debt = _calculateDebt(loanId);
        require(amountUsd18 <= debt, "Amount exceeds debt");

        // Register repayment in registry
        registry.registerRepayment(loanId, amountUsd18);

        // If fully repaid, return collateral
        if (amountUsd18 >= debt) {
            IERC20(vaultData.collateralToken).safeTransfer(msg.sender, vaultData.collateralAmount);
        }

        emit LoanRepaid(loanId, msg.sender, amountUsd18);

        // TODO: Accept repayment funds from user
    }

    // ==================== LIQUIDATION FUNCTION ====================

    function liquidate(uint256 loanId) external nonReentrant whenNotPaused {
        VaultLoanData storage vaultData = vaultLoans[loanId];
        require(vaultData.exists, "Loan not found");

        CreditRegistryV3.LoanRecord memory loan = registry.getLoan(loanId);
        require(loan.status == CreditRegistryV3.LoanStatus.Active, "Loan not active");

        bool canLiquidate = _checkLiquidationEligibility(loanId, vaultData, loan.borrower);

        if (!canLiquidate) {
            // Grace period started or still active - don't execute liquidation
            return;
        }

        _executeLiquidation(loanId, vaultData, loan.borrower);
    }

    function _checkLiquidationEligibility(
        uint256 loanId,
        VaultLoanData storage vaultData,
        address borrower
    ) private returns (bool) {
        uint256 debt = _calculateDebt(loanId);
        uint256 collateralUsd18 = _tokenAmountToUsd18(vaultData.collateralToken, vaultData.collateralAmount);

        ScoreOraclePhase3B.ScoreBreakdown memory score = oracle.computeScore(borrower);
        uint16 maxLtvPercent = _maxLtvForScore(score.overall);
        uint256 liquidationThreshold = maxLtvPercent + 10;

        bool thresholdExceeded = (debt * 100 > collateralUsd18 * liquidationThreshold);

        if (thresholdExceeded) {
            // Liquidation threshold exceeded - can liquidate immediately
            return true;
        }

        // Below liquidation threshold - check grace period
        uint256 gracePeriod = _gracePeriodForScore(score.overall);

        if (vaultData.graceStart == 0) {
            // Start grace period (transaction succeeds, but liquidation doesn't happen)
            vaultData.graceStart = block.timestamp;
            emit GracePeriodStarted(loanId, block.timestamp);
            return false;
        }

        // Grace period already started - check if expired
        if (block.timestamp >= vaultData.graceStart + gracePeriod) {
            return true; // Grace expired, can liquidate
        }

        return false; // Still in grace period
    }

    function _executeLiquidation(
        uint256 loanId,
        VaultLoanData storage vaultData,
        address borrower
    ) private {
        uint256 collateralAmt = vaultData.collateralAmount;
        address collateralTkn = vaultData.collateralToken;

        uint256 liquidatorReward = (collateralAmt * LIQUIDATOR_SHARE_BPS) / 10000;
        uint256 insuranceReward = (collateralAmt * INSURANCE_SHARE_BPS) / 10000;
        uint256 penaltyAmount = liquidatorReward + insuranceReward;
        uint256 remaining = collateralAmt - penaltyAmount;

        IERC20(collateralTkn).safeTransfer(msg.sender, liquidatorReward);
        if (insurancePool != address(0)) {
            IERC20(collateralTkn).safeTransfer(insurancePool, insuranceReward);
        }

        if (remaining > 0) {
            IERC20(collateralTkn).safeTransfer(borrower, remaining);
        }

        uint256 recoveredUsd18 = _tokenAmountToUsd18(collateralTkn, penaltyAmount);
        registry.registerLiquidation(loanId, recoveredUsd18);

        emit LoanLiquidated(loanId, borrower, msg.sender, collateralAmt);
    }

    // ==================== VIEW FUNCTIONS ====================

    function calculateDebt(uint256 loanId) external view returns (uint256) {
        return _calculateDebt(loanId);
    }

    function _calculateDebt(uint256 loanId) internal view returns (uint256) {
        CreditRegistryV3.LoanRecord memory loan = registry.getLoan(loanId);
        VaultLoanData memory vaultData = vaultLoans[loanId];

        if (loan.status != CreditRegistryV3.LoanStatus.Active) {
            return 0;
        }

        uint256 timeElapsed = block.timestamp - vaultData.startTimestamp;
        uint256 interest = (loan.principalUsd18 * vaultData.aprBps * timeElapsed) / (10000 * 365 days);

        return loan.principalUsd18 + interest - loan.repaidUsd18;
    }

    // ==================== INTERNAL HELPERS ====================

    function _tokenAmountToUsd18(address token, uint256 amount) internal view returns (uint256) {
        // For testnet, use mock price feeds
        // In production, use Chainlink price feeds
        address priceFeed = assets[token].priceFeed;
        require(priceFeed != address(0), "No price feed");

        // Simplified: assume price feed returns price with 8 decimals (Chainlink standard)
        (bool success, bytes memory data) = priceFeed.staticcall(
            abi.encodeWithSignature("latestAnswer()")
        );
        require(success, "Price feed call failed");
        int256 price = abi.decode(data, (int256));
        require(price > 0, "Invalid price");

        // Convert token amount to USD with 18 decimals
        uint256 decimals = _getTokenDecimals(token);
        uint256 valueUsd18 = (amount * uint256(price) * 1e18) / (10 ** decimals * 1e8);

        return valueUsd18;
    }

    function _getTokenDecimals(address token) internal view returns (uint256) {
        (bool success, bytes memory data) = token.staticcall(
            abi.encodeWithSignature("decimals()")
        );
        require(success, "Decimals call failed");
        return abi.decode(data, (uint256));
    }

    function _maxLtvForScore(uint16 overall) internal pure returns (uint16) {
        if (overall >= 90) return 90; // Platinum
        if (overall >= 75) return 80; // Gold
        if (overall >= 60) return 70; // Silver
        return 50; // Bronze
    }

    function _gracePeriodForScore(uint16 score) internal pure returns (uint256) {
        if (score >= 90) return GRACE_PLATINUM;
        if (score >= 75) return GRACE_GOLD;
        if (score >= 60) return GRACE_SILVER;
        return GRACE_BRONZE;
    }

    // ==================== ADMIN FUNCTIONS ====================

    function setAsset(address token, address priceFeed, bool allowed) external onlyOwner {
        assets[token] = Asset({
            allowed: allowed,
            priceFeed: priceFeed
        });
    }

    function setInsurancePool(address _insurancePool) external onlyOwner {
        insurancePool = _insurancePool;
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }
}

contracts/ScoreOraclePhase3B.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./CreditRegistryV3.sol";

/**
 * @title ScoreOraclePhase3B
 * @notice Complete 5-factor credit scoring oracle
 * @dev Phase 3B - Full implementation of S1-S5
 *
 * SCORING SYSTEM:
 * - S1: Repayment History (40% weight) - Range: 0-100
 * - S2: Collateral Utilization (20% weight) - Range: 0-100
 * - S3: Sybil Resistance (20% weight) - Range: -450 to +295, normalized to 0-100
 * - S4: Cross-Chain Reputation (10% weight) - Range: 0-100
 * - S5: Governance Participation (10% weight) - Range: 0-100
 *
 * Overall = (S1 * 0.40) + (S2 * 0.20) + (S3_norm * 0.20) + (S4 * 0.10) + (S5 * 0.10)
 */
contract ScoreOraclePhase3B is Ownable {
    // ==================== STATE ====================

    CreditRegistryV3 public registry;

    // Supported chain selectors for S4 (Chainlink CCIP)
    uint64[] public supportedChainSelectors;
    mapping(uint64 => bool) public isSupportedChain;

    // Tier thresholds (same as Phase 3)
    uint16 public constant TIER_SILVER = 60;
    uint16 public constant TIER_GOLD = 75;
    uint16 public constant TIER_PLATINUM = 90;

    // ==================== STRUCTS ====================

    struct ScoreBreakdown {
        uint16 overall;          // Final weighted score (0-100)
        uint8 s1_repayment;      // 0-100
        uint8 s2_collateral;     // 0-100
        uint8 s3_sybil;          // Normalized 0-100
        uint8 s4_crossChain;     // 0-100
        uint8 s5_governance;     // 0-100
        int16 s3_raw;            // Raw S3 score before normalization
    }

    enum ScoreTier { Bronze, Silver, Gold, Platinum }

    // ==================== EVENTS ====================

    event ChainSelectorAdded(uint64 indexed chainSelector);
    event ChainSelectorRemoved(uint64 indexed chainSelector);

    // ==================== CONSTRUCTOR ====================

    constructor(address payable _registry) Ownable(msg.sender) {
        registry = CreditRegistryV3(_registry);

        // Initialize with Arbitrum, Optimism, Base chain selectors (testnet)
        _addChainSelector(3478487238524512106); // Arbitrum Sepolia
        _addChainSelector(5224473277236331295); // Optimism Sepolia
        _addChainSelector(10344971235874465080); // Base Sepolia
    }

    // ==================== SCORING FUNCTIONS ====================

    /**
     * @notice Compute complete 5-factor credit score
     */
    function computeScore(address subject) external view returns (ScoreBreakdown memory) {
        uint8 s1 = _scoreRepaymentHistory(subject);
        uint8 s2 = _scoreCollateralUtilization(subject);
        int16 s3Raw = _scoreSybilResistance(subject);
        uint8 s3 = _normalizeS3(s3Raw);
        uint8 s4 = _scoreCrossChainReputation(subject);
        uint8 s5 = _scoreGovernanceParticipation(subject);

        // Weighted average
        uint256 weighted = (
            (uint256(s1) * 40) +
            (uint256(s2) * 20) +
            (uint256(s3) * 20) +
            (uint256(s4) * 10) +
            (uint256(s5) * 10)
        ) / 100;

        return ScoreBreakdown({
            overall: uint16(weighted),
            s1_repayment: s1,
            s2_collateral: s2,
            s3_sybil: s3,
            s4_crossChain: s4,
            s5_governance: s5,
            s3_raw: s3Raw
        });
    }

    // ==================== S1: REPAYMENT HISTORY (40%) ====================

    function _scoreRepaymentHistory(address subject) internal view returns (uint8) {
        uint256[] memory loanIds = registry.getLoanIdsByBorrower(subject);
        if (loanIds.length == 0) return 50; // Neutral for no history

        uint256 totalLoans = loanIds.length;
        uint256 repaidCount = 0;
        uint256 liquidatedCount = 0;

        for (uint256 i = 0; i < loanIds.length; i++) {
            CreditRegistryV3.LoanRecord memory loan = registry.getLoan(loanIds[i]);
            if (loan.status == CreditRegistryV3.LoanStatus.Repaid) {
                repaidCount++;
            } else if (loan.status == CreditRegistryV3.LoanStatus.Liquidated) {
                liquidatedCount++;
            }
        }

        // Formula: (repaid/total * 100) - (liquidations * 20)
        uint256 repaymentRate = (repaidCount * 100) / totalLoans;
        int256 score = int256(repaymentRate) - int256(liquidatedCount * 20);

        if (score < 0) return 0;
        if (score > 100) return 100;
        return uint8(uint256(score));
    }

    // ==================== S2: COLLATERAL UTILIZATION (20%) ====================

    function _scoreCollateralUtilization(address subject) internal view returns (uint8) {
        uint256[] memory loanIds = registry.getLoanIdsByBorrower(subject);
        if (loanIds.length == 0) return 50; // Neutral for no history

        uint256 totalCollateral = 0;
        uint256 totalBorrowed = 0;
        uint256 maxLtvCount = 0;
        uint256 totalLoans = loanIds.length;

        for (uint256 i = 0; i < loanIds.length; i++) {
            CreditRegistryV3.CollateralData memory colData = registry.getCollateralData(loanIds[i]);

            if (colData.collateralValueUsd18 > 0) {
                totalCollateral += colData.collateralValueUsd18;
                totalBorrowed += colData.principalUsd18;

                // Check if borrowed at max LTV (within 5%)
                uint256 actualLtv = (colData.principalUsd18 * 100) / colData.collateralValueUsd18;
                uint256 maxLtv = _maxLtvForScore(colData.userScoreAtBorrow);

                if (actualLtv >= (maxLtv * 95) / 100) {
                    maxLtvCount++;
                }
            }
        }

        if (totalBorrowed == 0) return 50; // No active borrows

        // Calculate average collateralization ratio
        uint256 avgRatio = (totalCollateral * 100) / totalBorrowed; // In percentage (200 = 200%)

        // Base score from collateralization ratio
        uint256 baseScore;
        if (avgRatio >= 200) baseScore = 100;      // 200%+
        else if (avgRatio >= 150) baseScore = 75;  // 150-200%
        else if (avgRatio >= 120) baseScore = 50;  // 120-150%
        else if (avgRatio >= 100) baseScore = 25;  // 100-120%
        else baseScore = 0;                        // Underwater

        // Penalty for max LTV borrowing
        uint256 maxLtvPct = (maxLtvCount * 100) / totalLoans;
        uint256 penalty = 0;
        if (maxLtvPct > 75) penalty = 40;
        else if (maxLtvPct > 50) penalty = 20;

        // Bonus for collateral diversity
        address[] memory uniqueAssets = registry.getUserCollateralAssets(subject);
        uint256 bonus = 0;
        if (uniqueAssets.length >= 3) bonus = 20;
        else if (uniqueAssets.length >= 2) bonus = 10;

        // Final score (clamped 0-100)
        int256 finalScore = int256(baseScore) - int256(penalty) + int256(bonus);
        if (finalScore < 0) return 0;
        if (finalScore > 100) return 100;
        return uint8(uint256(finalScore));
    }

    // ==================== S3: SYBIL RESISTANCE (20%) ====================

    function _scoreSybilResistance(address subject) internal view returns (int16) {
        int256 score = 0;

        // 1. KYC Verification (+150 or -150)
        if (registry.isKYCVerified(subject)) {
            score += 150;
        } else {
            score -= 150;
        }

        // 2. Wallet Age
        uint256 firstSeen = registry.getFirstSeen(subject);
        if (firstSeen == 0) {
            score -= 300; // New wallet
        } else {
            uint256 age = block.timestamp - firstSeen;
            if (age < 30 days) score -= 300;
            else if (age < 90 days) score -= 200;
            else if (age < 180 days) score -= 100;
            else if (age < 365 days) score -= 50;
            // else no penalty (365+ days)
        }

        // 3. Staking Bonus
        CreditRegistryV3.StakeInfo memory stake = registry.getStakeInfo(subject);
        if (stake.amount >= 1000 ether) score += 75;
        else if (stake.amount >= 500 ether) score += 50;
        else if (stake.amount >= 100 ether) score += 25;

        // 4. On-chain Activity (simplified - could integrate Trusta Labs for real tx count)
        // For now, use loan count as proxy for activity
        uint256 loanCount = registry.getLoanIdsByBorrower(subject).length;
        if (loanCount >= 10) score += 50;
        else if (loanCount >= 5) score += 30;
        else if (loanCount >= 3) score += 15;

        // Range: -450 to +295 (KYC-150 + WalletAge-300 + Staking75 + Activity50)
        return int16(score);
    }

    function _normalizeS3(int16 s3Raw) internal pure returns (uint8) {
        // S3 range: -450 to +295 (745 point range)
        // Normalize to 0-100
        int256 min = -450;
        int256 max = 295;
        int256 range = max - min; // 745

        int256 normalized = ((int256(s3Raw) - min) * 100) / range;

        if (normalized < 0) return 0;
        if (normalized > 100) return 100;
        return uint8(uint256(normalized));
    }

    // ==================== S4: CROSS-CHAIN REPUTATION (10%) ====================

    function _scoreCrossChainReputation(address subject) internal view returns (uint8) {
        uint256 chainCount = 0;
        uint256 totalCrossChainScore = 0;

        // Aggregate scores from other chains
        for (uint256 i = 0; i < supportedChainSelectors.length; i++) {
            uint64 chainSelector = supportedChainSelectors[i];
            CreditRegistryV3.CrossChainScore memory crossScore = registry.getCrossChainScore(subject, chainSelector);

            if (crossScore.updatedAt > 0) {
                chainCount++;
                totalCrossChainScore += crossScore.overallScore;
            }
        }

        if (chainCount == 0) {
            return 0; // Neutral if no cross-chain history
        }

        // Average cross-chain score
        uint256 avgCrossChainScore = totalCrossChainScore / chainCount;

        // Base score from cross-chain average
        uint256 baseScore;
        if (avgCrossChainScore >= 75) baseScore = 100;
        else if (avgCrossChainScore >= 60) baseScore = 75;
        else if (avgCrossChainScore >= 45) baseScore = 50;
        else if (avgCrossChainScore >= 30) baseScore = 25;
        else baseScore = 0;

        // Bonus for multi-chain presence
        uint256 bonus = 0;
        if (chainCount >= 3) bonus = 20;
        else if (chainCount >= 2) bonus = 10;

        uint256 finalScore = baseScore + bonus;
        if (finalScore > 100) return 100;
        return uint8(finalScore);
    }

    // ==================== S5: GOVERNANCE PARTICIPATION (10%) ====================

    function _scoreGovernanceParticipation(address subject) internal view returns (uint8) {
        CreditRegistryV3.GovernanceActivity memory activity = registry.getGovernanceActivity(subject);

        uint256 score = 0;

        // 1. Voting Activity (max 40 points)
        if (activity.voteCount >= 20) score += 40;
        else if (activity.voteCount >= 10) score += 30;
        else if (activity.voteCount >= 5) score += 20;
        else if (activity.voteCount >= 1) score += 10;

        // 2. Proposal Creation (max 30 points)
        if (activity.proposalCount >= 5) score += 30;
        else if (activity.proposalCount >= 3) score += 20;
        else if (activity.proposalCount >= 1) score += 10;

        // 3. Recent Activity (max 30 points)
        uint256 timeSinceLastVote = block.timestamp - activity.lastVoteTimestamp;
        if (activity.lastVoteTimestamp > 0) {
            if (timeSinceLastVote < 30 days) score += 30;
            else if (timeSinceLastVote < 90 days) score += 20;
            else if (timeSinceLastVote < 180 days) score += 10;
        }

        if (score > 100) return 100;
        return uint8(score);
    }

    // ==================== TIER & TERMS FUNCTIONS ====================

    function getScoreTier(uint16 overall) external pure returns (ScoreTier) {
        if (overall >= TIER_PLATINUM) return ScoreTier.Platinum;
        if (overall >= TIER_GOLD) return ScoreTier.Gold;
        if (overall >= TIER_SILVER) return ScoreTier.Silver;
        return ScoreTier.Bronze;
    }

    function getAPR(uint16 overall) external pure returns (uint16 aprBps) {
        if (overall >= TIER_PLATINUM) return 400;   // 4% APR
        if (overall >= TIER_GOLD) return 600;       // 6% APR
        if (overall >= TIER_SILVER) return 800;     // 8% APR
        if (overall >= 45) return 1000;             // 10% APR
        if (overall >= 30) return 1200;             // 12% APR
        return 1500;                                // 15% APR
    }

    function _maxLtvForScore(uint16 overall) internal pure returns (uint16) {
        if (overall >= TIER_PLATINUM) return 90;
        if (overall >= TIER_GOLD) return 80;
        if (overall >= TIER_SILVER) return 70;
        return 50; // Bronze
    }

    // ==================== ADMIN FUNCTIONS ====================

    function addChainSelector(uint64 chainSelector) external onlyOwner {
        _addChainSelector(chainSelector);
    }

    function _addChainSelector(uint64 chainSelector) private {
        require(!isSupportedChain[chainSelector], "Already supported");
        supportedChainSelectors.push(chainSelector);
        isSupportedChain[chainSelector] = true;
        emit ChainSelectorAdded(chainSelector);
    }

    function removeChainSelector(uint64 chainSelector) external onlyOwner {
        require(isSupportedChain[chainSelector], "Not supported");

        // Remove from array
        for (uint256 i = 0; i < supportedChainSelectors.length; i++) {
            if (supportedChainSelectors[i] == chainSelector) {
                supportedChainSelectors[i] = supportedChainSelectors[supportedChainSelectors.length - 1];
                supportedChainSelectors.pop();
                break;
            }
        }

        isSupportedChain[chainSelector] = false;
        emit ChainSelectorRemoved(chainSelector);
    }

    function getSupportedChainSelectors() external view returns (uint64[] memory) {
        return supportedChainSelectors;
    }
}

